body:has(.apm-header-vertical) {
  background-color: var(--bg-light);
}




/// Variables
$header-fw: 700;
:root {
  // Font Families
  --ff-base: 'Body', sans-serif;
  --ff-body: 'Body', sans-serif;
  --ff-head: 'Head', sans-serif;

  --mac-ff-head: 'Head', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  --mac-ff-body: 'Body', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  --mac-ff-deco: 'Deco', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;

  --mac-font-head: 'Head', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  --mac-font-body: 'Body', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  --mac-font-deco: 'Deco', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;

  // Animations
  --icon-transition: var(--transition);
  --shadow: var(--box-shadow-m);
  --padding: var(--fr-card-padding);
  --gap: var(--fr-card-gap);
  --hover-up: translateY(-.5em);

  // Breakpoints
  --bp-xl: 1366px;
  --bp-l: 992px;
  --bp-m: 768px;
  --bp-s: 479px;

  // Overlays
  --mac-overlay-fallback: var(--base-ultra-dark-trans-80);
  --mac-overlay: radial-gradient(circle, var(--base-ultra-dark-trans-80), var(--base-ultra-dark-trans-70));
}


/// Mixins
@mixin mac-btn {
  // variables
  --btn-background: var(--primary);
  --btn-background-hover: var(--primary-hover);
  --btn-text-color: var(--primary-ultra-light);
  --btn-text-color-hover: var(--primary-ultra-light);
  --btn-border-color: var(--primary);
  --btn-border-color-hover: var(--primary-hover);
  --btn-outline-background-hover: var(--primary-hover);
  --btn-outline-border-hover: var(--primary-hover);
  --btn-outline-text-color: var(--primary);
  --btn-outline-text-color-hover: var(--primary-ultra-light);
  --focus-color: var(--primary-dark);

  // styles
  padding-block: var(--btn-padding-block);
  padding-inline: var(--btn-padding-inline);
  min-inline-size: var(--btn-min-width);
  line-height: var(--btn-line-height);
  font-size: var(--btn-font-size, var(--text-m));
  font-weight: var(--btn-font-weight);
  font-style: var(--btn-font-style);
  text-decoration: var(--btn-text-decoration);
  text-transform: var(--btn-text-transform);
  letter-spacing: var(--btn-letter-spacing);
  border-width: var(--btn-border-width);
  border-style: var(--btn-border-style);
  border-radius: var(--btn-border-radius);
  display: var(--btn-display, inline-flex);
  text-align: center;
  justify-content: center;
  align-items: center;
  background: var(--btn-background);
  color: var(--btn-text-color);
  border-color: var(--btn-border-color);
  transition: all var(--transition-duration) var(--transition-timing);

  // hover
  &:hover {
      background: var(--btn-background-hover);
      border-color: var(--btn-border-color-hover);
      color: var(--btn-text-color-hover);
      text-decoration: var(--btn-text-decoration-hover);

  }
}

@mixin mac-load {
  @include mac-btn;
  --btn-background: var(--neutral);
  --btn-background-hover: var(--neutral-dark);
  --btn-text-color: var(--neutral-ultra-light);
  --btn-text-color-hover: var(--neutral-light);
  --btn-border-color: var(--neutral);
  --btn-border-color-hover: var(--neutral-dark);
  --btn-outline-background-hover: var(--neutral-dark);
  --btn-outline-border-hover: var(--neutral-dark);
  --btn-outline-text-color: var(--neutral);
  --btn-outline-text-color-hover: var(--neutral-light);
  --focus-color: var(--neutral-dark);
}



/// WSForm
// options
form.wsf-form {
  // fix phone flag dropdown alignment
  .iti__flag-container,
  .iti__selected-flag {
    height: var(--f-input-height, 5rem) !important;
  }
  // fix password meter alignment
  input[data-password-strength-meter] + ul {
    vertical-align: middle !important;
    top: calc((var(--f-input-height, 5rem) - ((var(--wsf-field-password-strength-meter-disc-size) * 3) + (var(--wsf-field-password-strength-meter-disc-gap) * 2))) / 2) !important;
  }
  option {
      color: var(--neutral);
      font-size: var(--text-m) !important;
  }
  optgroup {
      color: var(--neutral-trans-50);
      text-transform: uppercase;
      font-size: var(--text-s);
  }
  option {
      text-transform: initial !important;
      font-size: var(--text-m) !important;
  }
}

// fix buttons
button.wsf-button {
  font-weight: 700 !important;
  text-transform: uppercase !important;
  letter-spacing: 0.05em !important;
  transition: var(--transition) !important;
}

// hide asterisk
.wsf-required-wrapper {
  display: none !important;
  visibility: none !important;
}

// readonly field
.form--light {
  input[readonly] {
    background-color: var(--neutral-light) !important;
    color: var(--neutral-medium) !important;
    border-color: var(--neutral-light) !important;
  }
}

// disabled fields
.form--light, .form--dark {
    input, button {
        &[disabled] {
            opacity: 70% !important;
        }
    }
}
[disabled] {
  cursor: not-allowed;
}

// invalid fields
.form--light {
  .wsf-form .wsf-validated input.wsf-field:invalid {
    --wsf-field-color-background-invalid: var(--white);
  }
}
.form--dark {
  .wsf-form .wsf-validated input.wsf-field:invalid {
    --wsf-field-color-background-invalid: var(--base-ultra-dark);
  }
}

// status
.wsf-text-success, .wsf-text-danger, .wsf-text-warning, .wsf-invalid-feedback {
    font-weight: 700 !important;
    padding: calc(var(--btn-pad-y)/2) calc(var(--btn-pad-x)/2)!important;
  border-radius: var(--btn-border-radius) !important;
}

// success
.wsf-text-success {
    color: var(--success) !important;
    background-color: var(--success-ultra-light) !important;
}

// warning
.wsf-text-warning {
  color: var(--warning) !important;
  background-color: var(--warning-ultra-light) !important;
}

// danger
.wsf-text-danger, .wsf-invalid-feedback {
  color: var(--danger) !important;
  background-color: var(--danger-ultra-light) !important;
}

/// RankMath
// html sitemap
article:has(.rank-math-html-sitemap) {
  width: var(--content-width);
  max-width: 100%;
  padding-inline: var(--gutter);
  padding-block: var(--section-space-m);
  margin: auto;

  h1 {
    color: var(--neutral);
    font-size: var(--h1);
  }

  h2.rank-math-html-sitemap__title {
    color: var(--neutral);
    font-size: var(--h3);
    margin-inline-start: 0.5em;
  }

  ul.rank-math-html-sitemap__list {
    li::marker {
      color: var(--neutral-trans-30);
    }

    li.rank-math-html-sitemap__item {
      a {
        font-weight: 700;

        &:hover {
          color: var(--primary-hover);
        }
      }
    }

    span.rank-math-html-sitemap__date {
      background-color: var(--neutral-ultra-light);
      color: var(--neutral-trans-60);
      font-size: var(--text-xs);
      text-transform: uppercase;
      letter-spacing: 0.05em;
      font-weight: 700;
      padding: 0.25em 0.5em;
    }
  }
}

/// Bricks
// menu
.brxe-dropdown .brx-submenu-toggle {
    gap: 0.25em;
}
.brx-submenu-toggle button {
    font-size: 0.75em;
}

// lightbox
.pswp.brx {
  --pswp-bg: var(--neutral-trans-90) !important;
  --pswp-placeholder-bg: var(--neutral-trans-90) !important;
  --pswp-preloader-color: var(--white) !important;
  --pswp-preloader-color-secondary: var(--neutral-trans-90) !important;
  --pswp-icon-color: var(--white) !important;
  --pswp-icon-color-secondary: var(--neutral-trans-90) !important;
  --pswp-icon-stroke-color: var(--white) !important;
  --pswp-icon-stroke-width: var(--btn-border-size) !important;
  --pswp-error-text-color: var(--pswp-icon-color);
  -webkit-tap-highlight-color: var(--neutral) !important;
  backdrop-filter: blur(10px);

  .pswp__button {
    opacity: 1 !important;
  }

  .pswp__top-bar .pswp__button svg {
    height: 3em !important;
    width: 3em !important;
  }

  .pswp__counter {
    font-weight: 700 !important;
    font-size: var(--text-m) !important;
  }
}

// popup
.brx-popup-content {
  scrollbar-width: none;
  -ms-overflow-style: none;
  overflow-y: scroll;

  &::-webkit-scrollbar {
    display: none;
  }

  &, .brx-popup-backdrop {
    transition: var(--transition) !important;
  }
}

.brx-popup-backdrop {
  /* background-color: var(--neutral-trans-90) !important;
  background: radial-gradient(circle, var(--neutral-trans-80) 0%, var(--neutral) 100%) !important;*/
     background-color: var(--base-trans-80);
    backdrop-filter: blur(5px);
}

// popup - member approve status
.brxe-popup-1529 .brx-popup-content,
.brxe-popup-1531 .brx-popup-content,
.brxe-popup-1533 .brx-popup-content {
  box-shadow: var(--box-shadow-m);
}
div[data-popup-id="1531"], div[data-popup-id="1533"] {
  pointer-events: none;
}

// empty loop text
.bricks-posts-nothing-found {}

/// Frames
// modal
.fr-modal__overlay {
  background-color: var(--neutral-trans-60) !important;
  backdrop-filter: blur(10px);
}

.fr-modal__close-icon-wrapper {
  background-color: var(--danger) !important;
  border-radius: 50vw !important;
  transition: var(--transition) !important;

  i, svg {
    fill: var(--white) !important;
    color: var(--white) !important;
    font-size: var(--text-xl) !important;

    &:hover {
      scale: 0.8 !important;
    }
  }
}

/// EmbedPress
// hide credits
.embedpress-el-powered {
  display: none !important;
}





/// WordPress
// PDF embed button
* + .wp-block-file {
    margin-top: var(--content-gap) !important;
}
.wp-block-file__button {
  background: var(--primary) !important;
  color: var(--white) !important;
  font-weight: 700 !important;
  font-size: var(--text-m) !important;
  border-radius: var(--btn-radius) !important;
  padding-block: 0.25em;
  padding-inline: 0.75em;
}
div.wp-block-file > a:not(.wp-block-file__button) {
	color: black !important;
	font-weight: 700 !important;
	font-size: var(--text-m) !important;
	text-decoration: underline !important;
	text-decoration-color: var(--primary) !important;
	text-decoration-thickness: 2px !important;
	text-underline-offset: 2px !important;
}
.wp-block-file__embed {
	margin-block-end: 1em !important;
}

// default wp button
.wp-block-button:not([class*="btn--"]) {
  .wp-block-button__link {
    @include mac-btn;
    --btn-background: var(--base);
    --btn-background-hover: var(--base-hover);
    --btn-text-color: var(--base-ultra-light);
    --btn-text-color-hover: var(--base-ultra-light);
    --btn-border-color: var(--base);
    --btn-border-color-hover: var(--base-hover);
    --btn-outline-background-hover: var(--base-hover);
    --btn-outline-border-hover: var(--base-hover);
    --btn-outline-text-color: var(--base);
    --btn-outline-text-color-hover: var(--base-ultra-light);
    --focus-color: var(--base-dark);
  }
}

// media & text
.wp-block-media-text {
  > .wp-block-media-text__content {
    > p {
      margin-block-end: 1.2em;
    }
  }
}

@media (max-width: 600px) {
  .wp-block-media-text {
    &.is-stacked-on-mobile {
      > .wp-block-media-text__media {
        padding-block-end: 1.2em;
      }
      > .wp-block-media-text__content {
        padding: 0;
      }
    }
  }
}

// blockquote
.wp-block-quote {
  border-color: var(--primary);
}

// table
.wp-block-table {
  border-radius: 0;
  border: var(--border);
  tbody {
    tr {
      &:nth-child(even) {
        background-color: var(--neutral-ultra-light);
      }
      &:nth-child(odd) {
        background-color: var(--white);
      }
    }
  }
}




/// Mega Menu
// --V1.5.6.2 w/ ACSS
// DOCS HERE: https://docs.nickarce.com/

// header
header:has(.header) {
  --header-background: var(--white-trans-70) var(--noise-bg);
}
header .apm-subheader {
  background: var(--white) var(--noise-bg);
}
header .header {
  background: var(--header-background);
  padding-block: var(--space-xs);
  padding-inline: 0;
  backdrop-filter: blur(8px);

  .header__container {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  }
  .header__logo {
    height: auto;
    width: auto;
    border-radius: 0;

    img {
      border-radius: 0;
      width: auto;
    }
  }

  img {
    border-radius: 0 !important;
  }
}

/* -----------------------NAV ITEMS-------------------- */

// DO YOU WANT THE LAST NAV ITEM TO LOOK LIKE A BUTTON ON DESKTOP? https://nickarce.com/desktop-button/ **********
$last-nav-button-desktop: true; // SELECTION (true/false) *********

// DO YOU WANT THE LAST NAV ITEM TO LOOK LIKE A BUTTON ON MOBILE? https://nickarce.com/mobile-button/ **********
$last-nav-button-mobile: true; // SELECTION (true/false) *********

// IF USING BUTTON ON MOBILE: DO YOU WANT IT AT THE BOTTOM OF THE SCREEN https://nickarce.com/mobile-button-bottom/ **********
$last-nav-button-mobile-bottom: true; // SELECTION (true/false)

// STYLE LAST NAV BUTTON HERE ********
@mixin last-link-btn-style {
  // style button here
  --btn-background: var(--primary);
  --btn-background-hover: var(--primary-hover);
  --btn-text-color: var(--primary-ultra-light);
  --btn-text-color-hover: var(--primary-ultra-light);
  --btn-border-color: var(--primary);
  --btn-border-color-hover: var(--primary-hover);
  --btn-outline-background-hover: var(--primary-hover);
  --btn-outline-border-hover: var(--primary-hover);
  --btn-outline-text-color: var(--primary);
  --btn-outline-text-color-hover: var(--primary-ultra-light);
  --focus-color: var(--primary-dark);
  padding-block: var(--btn-padding-block);
  padding-inline: var(--btn-padding-inline);
  min-inline-size: var(--btn-min-width);
  line-height: var(--btn-line-height);
  font-size: var(--btn-font-size, var(--text-m));
  font-weight: var(--btn-font-weight);
  font-style: var(--btn-font-style);
  text-decoration: var(--btn-text-decoration);
  text-transform: var(--btn-text-transform);
  letter-spacing: var(--btn-letter-spacing);
  border-width: var(--btn-border-width);
  border-style: var(--btn-border-style);
  border-radius: var(--btn-border-radius);
  display: var(--btn-display, inline-flex);
  text-align: center;
  justify-content: center;
  align-items: center;
  background: var(--btn-background);
  color: var(--btn-text-color);
  border-color: var(--btn-border-color);
  transition: var(--transition);

  font-size: var(--text-s);

  //hover styling
  &:hover {
    background: var(--btn-background-hover);
    border-color: var(--btn-border-color-hover);
    color: var(--btn-text-color-hover);
    text-decoration: var(--btn-text-decoration-hover);
  }
}

// WHAT STYLES DO YOU WANT YOUR NAV LINKS? https://nickarce.com/link-styles/ ******
@mixin link-style {
  color: var(--text-dark);
  font-weight: $header-fw;

  //active page style
  &[aria-current='page'],
  &.aria-current {
  }
}

//WHAT STYLES DO YOU WANT YOUR NAV LINKS ON HOVER AND YOUR DROPDOWNS WHEN THEY ARE OPEN? *****
@mixin link-hover-style {
  color: var(--neutral-semi-light);

  //active page style
  &[aria-current='page'],
  &.aria-current {
  }
}

// DO YOU WANT DIFFERENT NAV LINK STYLING FOR STICKY HEADER ON SCROLL? https://nickarce.com/sticky-link-style/ ******
$sticky-header-styles: false; // SELECTION (true/false)

// WHAT STYLES DO YOU WANT THE LINKS ON A STICKY HEADER? ******
@mixin sticky-link-style {
  color: var(--text-dark);

  // active page style
  &[aria-current='page'],
  &.aria-current {
  }
}

// WHAT STYLES DO YOU WANT YOUR NAV LINKS ON HOVER AND YOUR DROPDOWNS WHEN THEY ARE OPEN ON STICKY? ******
@mixin sticky-link-hover-style {
  color: var(--text-dark);

  // active page style
  &[aria-current='page'],
  &.aria-current {
  }
}

// DESKTOP MENU ITEMS GAP https://nickarce.com/nav-items-gap/ ******
$nav-items-gap: var(--space-s);

// ACTIVE DESKTOP DROPDOWN ICON ANIMATION (if no animation select 'none') https://nickarce.com/dropdown-icon-animation/ ******
$active-dropdown-icon-anim: flip; // SELECTION (none/flip/rotate)

// MOBILE HAMBURGER ICON SIZE ******
$hamburger-icon-size: 3.2rem; // hamburger icon

// MOBILE HAMBURGER ICON COLOR ******
$hamburger-color: var(--text-dark);

// SET STICKY HAMBURGER COLOR MUST HAVE $sticky-header-styles set to true for it to work ******
$hamburger-sticky-color-choice: var(--text-dark);

// IF USING A CUSTOM HAMBURGER ICON DO YOU WANT TO SET THE FILL OR STROKE COLOR ******
$custom-hamburger-icon-fill-stroke: none; //SELECTION (none/fill/stroke)

/* --------------------MEGA MENU CONTENT DESKTOP & MOBILE----------------- */

// ARE YOU USING ACSS AUTO CONTENT GAP?
$fix-acss-content-gap: true; // SELECTION (true/false)

// WHAT BACKGROUND COLOR DO YOU WANT FOR DROPDOWN WRAPPER ******
$dropdown-background: var(--white);

// WHAT STYLES DO YOU WANT THE LINKS IN THE MENU DROPDOWN ON DESKTOP? https://nickarce.com/dropdown-links-style/ ******
@mixin dropdown-link-style {
  color: var(--text-dark);
  padding-block: calc(var(--space-xs) * 0.75);
  padding-inline: var(--space-xs);
  font-weight: $header-fw;

  // hover styling
  &:hover {
    color: var(--text-dark);
    background-color: var(--bg-light);
  }
}

// UPDATE THE DESKTOP MEGA DROPDOWN CONTAINER STYLE HERE https://nickarce.com/mega-menu-dropdown-style/ ******
@mixin mega-dropdown-content {
  // style here
  padding-block: var(--space-m);
  padding-inline: var(--space-m);
  gap: var(--space-m);
  box-shadow: 119px 360px 152px rgba(0, 0, 0, 0.01), 67px 203px 128px rgba(0, 0, 0, 0.05),
    30px 90px 95px rgba(0, 0, 0, 0.09), 7px 23px 52px rgba(0, 0, 0, 0.1),
    0px 0px 0px rgba(0, 0, 0, 0.1);
  flex-direction: row;
}

// UPDATE THE DESKTOP MENU DROPDOWN (non mega) CONTAINER STYLE HERE https://nickarce.com/menu-dropdown-style/ ******
@mixin standard-menu-dropdown-content {
  //style here
  min-width: fit-content;
  box-shadow: 119px 360px 152px rgba(0, 0, 0, 0.01), 67px 203px 128px rgba(0, 0, 0, 0.05),
    30px 90px 95px rgba(0, 0, 0, 0.09), 7px 23px 52px rgba(0, 0, 0, 0.1),
    0px 0px 0px rgba(0, 0, 0, 0.1);

  // link text length. this will help size the dropdown
  > li a,
  > li .brx-submenu-toggle {
    white-space: nowrap;

    // add border-block-end to all dropdown links
    @media (min-width: $navigation-breakpoint) {
      border-block-end: var(--border);
    }
  }

  // desktop dropdown styling
  @media (min-width: $navigation-breakpoint) {
    border: var(--border);
    border-radius: var(--radius);
    > li:last-child > a,
    > li:last-child > .brx-submenu-toggle {
      border-block: none;
    }
    > li:first-child > a,
    > li:first-child > .brx-submenu-toggle {
      border-radius: var(--radius) var(--radius) 0 0;
    }
    > li:last-child > a,
    > li:last-child > .brx-submenu-toggle {
      border-radius: 0 0 var(--radius) var(--radius);
    }
  }
}

// DROPDOWN OPEN ANIMATION ON DESKTOP ******
$fade-up-closed: 32px; // STARTING POSITION (not active)
$fade-up-open: 8px; // ENDING POSITION (active)

// UPDATE THE MOBILE MEGA DROPDOWN CONTAINER STYLE HERE https://nickarce.com/mobile-mega-content/ ******
@mixin dropdown-content-mobile {
  // style here
  flex-direction: column;
}

// DO YOU WANT TO HIDE THE SCROLLBAR FOR THE DROPDOWN CONTENT ON MOBILE? ******
$hide-scrollbar-sub-nav-scrollbar: true; // SELECTION (true/false)

/* -----------------------MOBILE SETTINGS-------------------- */

// WHEN WILL YOUR NAVIGATION CHANGE TO MOBILE? ******
$navigation-breakpoint: 1100px;

// SET MOBILE PADDING ******
:root {
  --mm-pad-v: var(--space-m); // vertical spacing to be used throughout mobile design
  --mm-pad-h: var(--gutter); // horizontal spacing to be used throughout mobile design
}

// WHERE DO YOU WANT THE MOBILE MENU TO SLIDE OPEN FROM? ******
$mobile-menu-slide-start-point: right; // SELECTION (left/right/top/bottom)

// HOW LONG SHOULD THE SLIDE TAKE? ******
$mobile-menu-slide-transition-time: 0.2s;

// WHAT TRANSITION EFFECT DO YOU WANT? ******
$mobile-menu-slide-transition-effect: cubic-bezier(0.79, 0.14, 0.15, 0.86);

// MOBILE MENU BACKGROUND COLOR ******
$mobile-background-color: var(--white);

// MOBILE MENU NAV LINK STYLE https://nickarce.com/mobile-menu-nav-items/ ******
@mixin mobile-nav-list-style {
  // using those spacing values
  padding-block: var(--mm-pad-v);
  // padding-inline: var(--mm-pad-h);
  // gap: var(--space-xs);
}

// MOBILE MENU NAV LINK STYLE https://nickarce.com/mobile-menu-nav-item/ ******
@mixin mobile-link-style {
  color: var(--text-dark);
  padding-block: calc(var(--mm-pad-v) / 2);
  padding-inline: var(--mm-pad-h);
  background-color: var(--white);
  border-radius: 0;
  text-wrap: pretty;
  font-weight: $header-fw;

  //hover style
  &:hover {
    background-color: var(--base-ultra-light);
    color: var(--neutral);
  }

  //active page style
  &[aria-current='page'],
  &.aria-current {
  }
}

//DO YOU WANT ANY PADDING ON THE LAST NAV ITEM ON MOBILE? must have $last-nav-button-mobile set to 'true' *****
$mobile-last-link-padding-block: unset;
$mobile-last-link-padding-inline: unset;

// SET MOBILE LINK BOTTOM BORDER CSS. USE 'none' IF YOU DONT WANT BORDERS https://nickarce.com/mobile-menu-border/ ******
$mm-border: var(--border); // border value (like: 1px solid red) or none

// DO YOU WANT A MOBILE LOGO? (add logo under nav nestable > nav items wrapper > mobile nav > mobile logo) ******
$mobile-logo: true; // SELECTION (true/false)

// WHAT HEIGHT DO YOU WANT THE LOGO? ******
$mobile-logo-height: 3.2rem;

// BACK BUTTON STYLING https://nickarce.com/back-button-styling/ ******
.mm-close-dropdown-btn {
  background-color: unset;
  gap: 0.5em;
  padding-inline-start: unset;
  font-weight: $header-fw;

  svg,
  i {
  }
}

// MOBILE CLOSE ICON SIZE ******
$close-icon-size: 3.2rem; // close icon

// MOBILE CLOSE ICON COLOR ******
$close-icon-color: var(--text-dark);

// IF USING A CUSTOM CLOSE ICON DO YOU WANT TO SET THE FILL OR STROKE COLOR ******
$custom-close-icon-fill-stroke: none; // SELECTION(none/fill/stroke)

/* ----------------------YOUR CUSTOM CSS--------------------- */

// Global Mega Menu Styling
.mm {
  --submenu-toggle-gap: 0.5em;
  --caret-size: 0.75em;
  --back-caret-size: 1em;
  --mobile-caret-size: var(--caret-size);

  //nest items here
  .brx-submenu-toggle {
    gap: var(--submenu-toggle-gap);

    svg,
    button svg {
      font-size: var(--caret-size);
