      font-size: var(--back-caret-size);
      height: var(--back-caret-size);
      width: var(--back-caret-size);
    }
  }
}

// Specifically Not Mobile Menu
.mm:not(.brx-open) {
  //nest items here
}

// Mega Menu Styling Only At Breakpoint
@media (max-width: $navigation-breakpoint) {
  .mm {
    .brx-submenu-toggle {
      svg,
      button svg {
        font-size: var(--mobile-caret-size);
        width: var(--mobile-caret-size);
        height: var(--mobile-caret-size);
      }
    }

    .mm__nav-items-wrapper {
      ul.mm__nav-items {
        padding-block-start: 0 !important;
        gap: 0;
      }
    }

    .mm__nav-items li.brxe-dropdown:not(.brx-has-megamenu) .brx-dropdown-content {
      padding-block: 0 !important;
      padding-inline: 0 !important;
    }
  }

  .mm__nav-items-wrapper ul.mm__nav-items > li:last-of-type {
    padding-inline: var(--mm-pad-h) !important;
  }
}

// Mega Menu Styling On Mobile Menu
.mm.brx-open {
  //nest items here
}

/* -----------------------END OF YOUR CUSTOM CSS-------------------- */

/* -----------------------LOGIC *IGNORE*-------------------- */

$megamenu-selector: '.brxe-dropdown.brx-has-megamenu';
$standardmenu-selector: '.brxe-dropdown:not(.brx-has-megamenu)';
$dropdown-content-selector: '.brx-dropdown-content';
$bothmenu-selector: '.brxe-dropdown';

$mobile-menu-slide-start-points: (
  left: translateX(-100%),
  right: translateX(100%),
  top: translateY(-100%),
  bottom: translateY(100%),
);

@mixin last-link-style-mobile {
  padding-block: var(--mm-pad-v);
  padding-inline: var(--mm-pad-h);
  text-align: center;
}

@mixin mobile-nav-items-style {
  > .mm__nav-link {
    @include mobile-link-style();
    width: 100%;
  }

  > .brx-submenu-toggle {
    @include mobile-link-style();

    svg {
      color: inherit;
      font-weight: inherit;
    }

    > a {
      color: inherit;
      font-weight: inherit;
      text-decoration: inherit;
    }
  }
}

@mixin unopened-dropdown-content {
  display: flex;
  position: fixed !important;
  inset-block: var(--mobile-top-nav-height) 0;
  @layer bricks.reset {
    inset-inline-start: 100% !important;
  }
  transition-property: inset-inline-start, opacity, visibility;
  transition-duration: $mobile-menu-slide-transition-time;
  display: flex;
  padding-block: var(--mm-pad-v);
  padding-inline: var(--mm-pad-h);
  width: 100%;
  overflow-x: hidden;
  @include dropdown-content-mobile();
  background-color: $mobile-background-color;
  border-radius: 0;
  box-shadow: none;
  opacity: 1;

  > * {
    max-width: 100%;
  }
}

@each $key, $value in $mobile-menu-slide-start-points {
  @if $mobile-menu-slide-start-point == $key {
    $mobile-menu-slide-start-point: $value;
  }
}

@if $sticky-header-styles {
  header.scrolling .mm .mm__toggle-open {
    color: $hamburger-sticky-color-choice;
  }
}

@if $sticky-header-styles and $last-nav-button-desktop {
  header.scrolling .mm:not(.brx-open) .mm__nav-items {
    > li:not(:last-of-type) {
      > a {
        @include sticky-link-style();

        &:hover {
          @include sticky-link-hover-style();
        }
      }
      > .brx-submenu-toggle {
        @include sticky-link-style();

        a,
        span,
        svg {
          color: inherit;
        }
      }

      &.open > .brx-submenu-toggle,
      &:hover > .brx-submenu-toggle {
        @include sticky-link-hover-style();
      }
    }
  }
}

@if $sticky-header-styles and $last-nav-button-desktop == false {
  header.scrolling .mm:not(.brx-open) .mm__nav-items {
    > li {
      > a {
        @include sticky-link-style();

        &:hover {
          @include sticky-link-hover-style();
        }
      }
      > .brx-submenu-toggle {
        @include sticky-link-style();

        a,
        span,
        svg {
          color: inherit;
        }
      }

      &.open > .brx-submenu-toggle,
      &:hover > .brx-submenu-toggle {
        @include sticky-link-hover-style();
      }
    }
  }
}

@if $fix-acss-content-gap == true {
  .mm__nav-items-wrapper,
  #{$dropdown-content-selector} {
    gap: unset;
  }
}

/* -----------------------END OF LOGIC-------------------- */

/* -----------------------REST OF CSS BELOW--------------------*/

// global menu styling
.mm {
  //mega dropdown
  #{$megamenu-selector} {
    #{$dropdown-content-selector} {
      @include mega-dropdown-content();
    }
  }

  //standard dropdown
  #{$standardmenu-selector} {
    #{$dropdown-content-selector} {
      @include standard-menu-dropdown-content();
    }
  }

  .mm__mobile-nav {
    display: none;
  }

  .mm-close-dropdown-btn {
    position: absolute;
    inset-inline-start: var(--mm-pad-h);
  }
}

@media (min-width: $navigation-breakpoint) {
  .mm {
    //standard dropdowns
    #{$standardmenu-selector} {
      #{$dropdown-content-selector} {
        .menu-item a,
        #{$standardmenu-selector} .brx-submenu-toggle {
          @include dropdown-link-style();
        }

        .menu-item a {
          width: 100%;
        }

        .brx-submenu-toggle > a {
          color: inherit;
          font-weight: inherit;
          text-transform: inherit;
        }
      }

      //sub standard dropdowns
      #{$standardmenu-selector} {
        .brx-submenu-toggle {
          justify-content: space-between;

          svg {
            transform: rotate(-90deg);
          }
        }
      }
    }

    //both dropdowns
    #{$bothmenu-selector} {
      #{$dropdown-content-selector} {
        background-color: $dropdown-background;
      }
    }

    .mm__nav-items-wrapper {
      transition-duration: 0s;
    }

    //nav-items
    .mm__nav-items {
      gap: $nav-items-gap;

      > li {
        // offset if max-width on ACSS text is changed.
        max-inline-size: max-content;
      }

      > #{$bothmenu-selector} {
        > .brx-submenu-toggle button svg {
          transform: rotate(0);
          transition: transform 0.2s ease-in-out;
        }

        > #{$dropdown-content-selector} {
          transform: translateY($fade-up-closed);
        }

        &.open {
          > #{$dropdown-content-selector} {
            transform: translateY($fade-up-open);
          }

          @if $active-dropdown-icon-anim == rotate {
            > .brx-submenu-toggle button svg {
              transform: rotate(-180deg);
            }
          } @else if $active-dropdown-icon-anim == flip {
            > .brx-submenu-toggle button svg {
              transform: rotatex(180deg);
            }
          }
        }
      }

      @if $last-nav-button-desktop {
        > li:last-of-type {
          a.mm__nav-link,
          > .brx-submenu-toggle {
            @include last-link-btn-style();
            width: 100%;
          }

          .brx-submenu-toggle {
            a {
              color: inherit;
            }
          }
        }

        > li:not(:last-of-type) {
          > a {
            @include link-style();

            &:hover {
              @include link-hover-style();
            }
          }
          > .brx-submenu-toggle {
            @include link-style();

            a,
            span,
            svg {
              color: inherit;
            }
          }

          &.open > .brx-submenu-toggle,
          &:hover > .brx-submenu-toggle {
            @include link-hover-style();
          }
        }
      } @else {
        > li {
          > a {
            @include link-style();

            &:hover {
              @include link-hover-style();
            }
          }
          > .brx-submenu-toggle {
            @include link-style();

            a,
            span,
            svg {
              color: inherit;
            }
          }

          &.open > .brx-submenu-toggle,
          &:hover > .brx-submenu-toggle {
            @include link-hover-style();
          }
        }
      }
    }
  }
}

// at breakpoint styling
@media (max-width: $navigation-breakpoint) {
  .mm {
    .mm__toggle-open {
      display: flex !important;
      --brxe-toggle-scale: 1;
      color: $hamburger-color;
      font-size: $hamburger-icon-size;

      svg {
        width: $hamburger-icon-size;
        height: auto;
        color: inherit;

        path {
          @if $custom-hamburger-icon-fill-stroke == stroke {
            stroke: currentColor;
          }

          @if $custom-hamburger-icon-fill-stroke == fill {
            fill: currentColor;
          }
        }
      }
    }

    .mm__mobile-nav {
      padding-block: var(--mm-pad-v);
      padding-inline: var(--mm-pad-h);
      border-block-end: $mm-border;
      display: flex;

      .mm-close-dropdown-btn {
        opacity: 0;
        visibility: hidden;
        transform: translatex(100%);
        transition: opacity $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect,
          visibility $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect,
          transform $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect;
      }

      &[data-submenu-open='True'] {
        .mm-close-dropdown-btn {
          visibility: visible;
          opacity: 1;
          transform: translatex(0);
        }
      }

      @if $mobile-logo {
        align-items: center;
        flex-direction: row-reverse;
        justify-content: space-between;
      } @else {
        align-items: flex-end;
      }
    }

    //standard dropdown
    .mm__nav-items #{$standardmenu-selector} {
      #{$dropdown-content-selector} {
        li > a {
          @include mobile-link-style();
          width: 100%;
        }

        #{$standardmenu-selector} .brx-submenu-toggle {
          @include mobile-link-style();

          svg {
            color: inherit;
            font-weight: inherit;
          }

          > a {
            color: inherit;
            font-weight: inherit;
            text-decoration: inherit;
          }
        }
      }
    }

    //both dropdowns
    .mm__nav-items #{$bothmenu-selector} {
      position: static;

      > #{$dropdown-content-selector} {
        @include unopened-dropdown-content();
      }

      > .brx-submenu-toggle {
        justify-content: space-between;
      }

      > .brx-submenu-toggle button {
        svg {
          transition: none;
          transform: rotate(-90deg);
        }
      }

      // when dropdown is open
      &.open {
        > #{$dropdown-content-selector} {
          @layer bricks.reset {
            inset-inline-start: 0% !important;
          }
        }
      }

      @if $hide-scrollbar-sub-nav-scrollbar {
        #{$dropdown-content-selector} {
          -ms-overflow-style: none; /* IE and Edge scrollbar */
          scrollbar-width: none; /* Firefox scrollbar */
          &::-webkit-scrollbar {
            display: none;
          } // Chrome scrollbar
        }
      }
    }

    //nav items wrapper
    .mm__nav-items-wrapper {
      position: fixed;
      inset: 0;
      background-color: $mobile-background-color;
      z-index: 1001;
      transform: $mobile-menu-slide-start-point;
      display: flex;
      visibility: hidden;
      transition: transform $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect,
        visibility $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect;

      .mm__toggle-close {
        display: flex;
        font-size: $close-icon-size;
        color: $close-icon-color;

        svg {
          width: $close-icon-size;
          height: auto;
          color: inherit;

          path {
            @if $custom-close-icon-fill-stroke == stroke {
              stroke: currentColor;
            }
            @if $custom-close-icon-fill-stroke == fill {
              fill: currentColor;
            }
          }
        }
      }
      //nav-items
      ul.mm__nav-items {
        position: relative;
        overflow-x: hidden;
        overflow-y: auto;
        display: flex;
        flex: 1;
        justify-content: flex-start;
        background-color: $mobile-background-color;
        -ms-overflow-style: none; /* IE and Edge scrollbar */
        scrollbar-width: none; /* Firefox scrollbar */
        &::-webkit-scrollbar {
          display: none;
        } // Chrome scrollbar
        align-items: stretch;
        bottom: 0;
        flex-direction: column;
        flex-wrap: nowrap;
        inset-inline: 0;
        @include mobile-nav-list-style;
        transition: visibility $mobile-menu-slide-transition-time
          $mobile-menu-slide-transition-effect;

        @layer bricks.reset {
          opacity: 1 !important;
        }

        @if $last-nav-button-mobile {
          > li:last-of-type {
            padding-block: $mobile-last-link-padding-block;
            padding-inline: $mobile-last-link-padding-inline;

            .mm__nav-link,
            > .brx-submenu-toggle {
              @include last-link-btn-style();
              width: 100%;
              > a {
                color: inherit;
                font-weight: inherit;
              }
            }
          }

          > li:not(:last-of-type) {
            border-block-end: $mm-border;
            @include mobile-nav-items-style();
          }
        } @else {
          > li {
            border-block-end: $mm-border;
            @include mobile-nav-items-style();
          }
        }

        @if $last-nav-button-mobile and $last-nav-button-mobile-bottom {
          > li:last-of-type {
            margin-block-start: auto;
            a.mm__nav-link,
            #{$megamenu-selector},
            #{$standardmenu-selector} {
              text-align: center;
            }
          }
        }
      }
    }

    .mm__nav-items li#{$standardmenu-selector} {
      #{$dropdown-content-selector} {
        @include mobile-nav-list-style;

        .menu-item,
        #{$standardmenu-selector} {
          border-block-end: $mm-border;
        }
      }
    }

    @if $mobile-logo {
      .mm__mobile-logo {
        display: block;
        opacity: 1;
        transform: translatex(0);
        transition: opacity $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect,
          visibility $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect,
          transform $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect;

        img {
          height: $mobile-logo-height;
          width: auto;
        }
      }

      .mm__mobile-nav[data-submenu-open='True'] {
        .mm__mobile-logo {
          visibility: hidden;
          opacity: 0;
          transform: translatex(-100%);
          transition: opacity
              calc(#{$mobile-menu-slide-transition-time} / 1.5)
              $mobile-menu-slide-transition-effect,
            visibility $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect,
            transform $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect;
          transition: opacity
              $mobile-menu-slide-transition-time
              $mobile-menu-slide-transition-effect,
            visibility $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect,
            transform $mobile-menu-slide-transition-time $mobile-menu-slide-transition-effect;
        }
      }
    }
  }

  //rtl specific overrides
  :where(html[dir='RTL']) .mm {
    // dropdown icons change rotation
    .mm__nav-items #{$bothmenu-selector} {
      > .brx-submenu-toggle button {
        svg {
          transform: rotate(90deg);
        }
      }
    }

    .mm__mobile-nav {
      // change direction of close dropdown btn animation
      .mm-close-dropdown-btn {
        transform: translatex(-100%);
        flex-direction: row-reverse;
      }
      @if $mobile-logo {
        // change mobile logo btn animation
        &[data-submenu-open='True'] .mm__mobile-logo {
          transform: translatex(100%);
        }
      }
    }
  }
}

// when open on mobile
.mm.brx-open {
  .mm__nav-items-wrapper {
    visibility: visible;
    transform: translateX(0);
  }

  .mm__nav-items {
    @layer bricks.reset {
      visibility: visible !important;
    }
  }
}
@media (max-width: $navigation-breakpoint) {
  // if admin bar is shown offset mobile menu
  body.bricks-is-frontend.admin-bar .mm__nav-items-wrapper {
    top: var(--wp-admin--admin-bar--height) !important;

    .mm__nav-items {
      top: unset;
    }
  }
}





/// --- FILTERS ---

/// BRX Filter - Utilities
@mixin filter-tokens {
  // Animation
  --mac-transition: none;
}

@mixin filter-choice-tokens {
  @include filter-tokens;
  // Layout
  --mac-list-direction: column;
  --mac-row-gap: 1ex;
  --mac-item-gap: 1ex;
  --mac-item-indent: 1ex;
  --mac-input-offset-block: 0.25ex;
  // Opacity
  --mac-opacity-disabled: 0.5;
  --mac-count-opacity: 0.5;
  --mac-count-opacity-alt: 1;
  // Typography
  --mac-font-size: var(--text-xs);
  --mac-line-height: 1;
  // Color
  --mac-text-color: var(--text-color);
  --mac-text-color-hover: var(--primary-hover);
  --mac-count-color: currentcolor;
  // Color - Active
  --mac-input-color-active: currentcolor;
  --mac-input-color-active-hover: currentcolor;
  --mac-text-color-active: var(--primary);
  --mac-text-color-active-hover: var(--primary);
  --mac-count-color-active: currentcolor;
  // Color - Disabled
  --mac-text-color-disabled: currentcolor;
  --mac-count-color-disabled: currentcolor;
}

@mixin filter-button-tokens {
  @include filter-tokens;
  // Animation
  --mac-btn-transition: var(--mac-transition);
  // Layout
  --mac-btn-padding-block: 0.5ex;
  --mac-btn-padding-inline: 1ex;
  --mac-btn-padding: var(--mac-btn-padding-block) var(--mac-btn-padding-inline);
  --mac-btn-gap: var(--mac-item-gap);
  // Border
  --mac-btn-radius: var(--btn-radius);
  --mac-btn-border: var(--border);
  // Color
  --mac-btn-text-color: var(--text-color);
  --mac-btn-border-color: var(--neutral-semi-light);
  --mac-btn-background: var(--white);
  --mac-btn-count-color: currentcolor;
  // Color - Hover
  --mac-btn-text-color-hover: var(--primary-hover);
  --mac-btn-border-color-hover: var(--primary-hover);
  --mac-btn-background-hover: var(--primary-ultra-light);
  --mac-btn-count-color-hover: currentcolor;
  // Color - Active
  --mac-btn-text-color-active: var(--primary-ultra-light);
  --mac-btn-border-color-active: var(--primary);
  --mac-btn-background-active: var(--primary);
  --mac-btn-count-color-active: currentcolor;
  // Color - Active - Hover
  --mac-btn-text-color-active-hover: var(--primary-ultra-light);
  --mac-btn-border-color-active-hover: var(--primary-hover);
  --mac-btn-background-active-hover: var(--primary-hover);
  --mac-btn-count-color-active-hover: currentcolor;
  // Disabled
  --mac-btn-opacity-disabled: 0.5;
  --mac-btn-text-color-disabled: var(--neutral);
  --mac-btn-border-color-disabled: currentcolor;
  --mac-btn-background-disabled: var(--neutral-light);
  --mac-btn-count-color-disabled: currentcolor;
}

@mixin filter-range-tokens {
  @include filter-tokens;
  // Base
  --mac-base-radius: var(--radius);
  --mac-base-height: 0.1em;
  --mac-base-color: var(--neutral-light);
  // Track
  --mac-track-thickness: 0.1em;
  --mac-track-color: var(--primary);
  --mac-track-radius: 5px;
  --mac-handle-color: var(--primary);
  // Value
  --mac-value-space-top: 1em;
  --mac-value-padding: 0.5ex 1.5ex;
  --mac-value-gap: 1em;
  --mac-value-font-size: var(--text-s);
  --mac-value-text-color: var(--neutral);
  --mac-value-weight: 700;
  --mac-value-radius: var(--radius);
  --mac-value-background: var(--neutral-ultra-light);
    // Input
  --mac-input-direction: row;
  --mac-input-gap: var(--fr-card-gap);
}

@mixin filter-input-tokens {
  @include filter-tokens;
  // Animation
  --mac-input-transition: var(--mac-transition);
  // Layout
  --mac-input-padding: 0 0.75em;
  // Typography
  --mac-input-font-size: var(--text-s);
  --mac-input-text-weight: 700;
  --mac-input-line-height: 4rem;
  --mac-input-text-align: left;
  // Border
  --mac-input-radius: var(--radius);
  --mac-input-border: var(--border);
  --mac-input-border-width: 1px;
  // Color
  --mac-input-text-color: var(--text-color);
  --mac-input-border-color: var(--neutral-semi-light);
  --mac-input-background: var(--white);
  // Color - Hover
  --mac-input-text-color-hover: var(--primary-hover);
  --mac-input-border-color-hover: var(--primary-hover);
  --mac-input-background-hover: var(--mac-input-background);
  // Placeholder
  --mac-input-placeholder-color: currentcolor;
  --mac-input-placeholder-opacity: 0.5;
  // Option
  --mac-option-text-color: var(--text-color);
  --mac-option-background: var(--white);
  // Option - Disabled
  --mac-option-text-color-disabled: var(--neutral-semi-light);
  --mac-option-background-disabled: var(--neutral-ultra-light);
}

@mixin filter-input {
  @include filter-input-tokens;

  // Input
  transition: var(--mac-input-transition);
  outline: none;
  padding: var(--mac-input-padding);
  width: 100%;
  max-width: 100%;
  display: flex;
  font-size: var(--mac-input-font-size);
  color: var(--mac-input-text-color);
  font-weight: var(--mac-input-text-weight);
  line-height: var(--mac-input-line-height);
  text-align: var(--mac-input-text-align);
  border-radius: var(--mac-input-radius);
  border: var(--mac-input-border);
  border-width: var(--mac-input-border-width);
  border-color: var(--mac-input-border-color);
  background-color: var(--mac-input-background);
  box-shadow: none;

  // Placeholder
  &::placeholder {
    transition: var(--mac-input-transition);
    color: var(--mac-input-placeholder-color);
    opacity: var(--mac-input-placeholder-opacity);
  }

  // Hover
  &:hover {
    color: var(--mac-input-text-color-hover);
    border-color: var(--mac-input-border-color-hover);
    background-color: var(--mac-input-background-hover);
  }
}



/// BRX Filter - Choice
ul.brxe-filter-checkbox, ul.brxe-filter-radio {
  @include filter-choice-tokens;

  // List
  list-style-type: none;
  text-indent: 0;
  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: var(--mac-list-direction);
  gap: var(--mac-row-gap);

  // Item
  li {
    opacity: 1;
    transition: var(--mac-transition);
    list-style-type: none;
    text-indent: 0;
    margin: 0;
    padding: 0;
    display: flex;
    font-size: var(--mac-font-size);
    color: var(--mac-text-color);
    line-height: var(--mac-line-height);

    // Item - Hover
    &:hover {
      color: var(--mac-text-color-hover);
      label {
        input {
          accent-color: var(--mac-input-color-active-hover);
          span.brx-option-active {
            span.brx-option-count {
              opacity: var(--mac-count-opacity-alt);
            }
          }
        }
      }
    }

    // Item - Active
    &.brx-option-active {
      color: var(--mac-text-color-active);
      label.brx-option-active {
        input[checked="checked"] {}
        span.brx-option-active {
          span.brx-option-count {
            opacity: var(--mac-count-opacity-alt);
            color: var(--mac-count-color-active);
          }
        }
      }

      // Item - Active - Hover
      &:hover {
        color: var(--mac-text-color-active-hover);
        label.brx-option-active {
          input[checked="checked"] {}
          span.brx-option-active {
            span.brx-option-count {
              opacity: var(--mac-count-opacity-alt);
            }
          }
        }
      }
    }

    // Item - Disabled
    &:has(.brx-option-disabled) {
      opacity: var(--mac-opacity-disabled);
      color: var(--mac-text-color-disabled);
      label.brx-option-disabled {
        user-select: none;
        pointer-events: none;
        cursor: default;
        input {
          span.brx-option-text {
            span.brx-option-count {
              opacity: var(--mac-count-opacity-alt);
              color: var(--mac-count-color-disabled);
            }
          }
        }
      }

      // Item - Disabled - Hover
      &:hover {
        color: var(--mac-text-color-disabled);
        label.brx-option-disabled {
          input {
            span.brx-option-text {
              span.brx-option-count {
                opacity: var(--mac-count-opacity-alt);
              }
            }
          }
        }
      }
    }

    // Label
    label {
      transition: var(--mac-transition);
      cursor: pointer;
      margin: 0;
      display: flex;
      flex-direction: row;
      align-items: start;
      gap: var(--mac-item-gap);
      font: inherit;
      font-size: inherit;
      color: currentcolor;
      line-height: inherit;

      // Input
      input {
        transition: var(--mac-transition);
        outline: none;
        margin-block-start: var(--mac-input-offset-block);
        padding: 0;
        width: auto;
        font-size: inherit;
        color: currentcolor;
        line-height: inherit;
        border: var(--border);
        border-style: solid;
        border-width: 1px;
        box-shadow: none;
        accent-color: var(--mac-input-color-active);
      }

      // Text
      span.brx-option-text {
        transition: var(--mac-transition);
        display: inline-flex;
        gap: var(--mac-item-gap);
        font-size: inherit;
        color: currentcolor;
        line-height: inherit;

        // Count
        span.brx-option-count {
          transition: var(--mac-transition);
          opacity: var(--mac-count-opacity);
          font-size: inherit;
          color: var(--mac-count-color);
          line-height: inherit;
        }
      }
    }
  }
}



/// BRX Filter - Button
ul.brxe-filter-checkbox[data-mode="button"], ul.brxe-filter-radio[data-mode="button"] {
  @include filter-button-tokens;

  // Overrides
  --mac-item-indent: 0;
  --mac-list-direction: column;

  // Item
  li:has(.bricks-button) {
    transition: var(--mac-btn-transition);
    color: var(--mac-btn-text-color);

    // Item - Hover
    &:hover {
      color: var(--mac-btn-text-color-hover);
      label {
        span.brx-option-text.bricks-button {
          border-color: var(--mac-btn-border-color-hover);
          background-color: var(--mac-btn-background-hover);
          span.brx-option-count {
            color: var(--mac-btn-count-color-hover);
          }
        }
      }
    }

    // Item - Active
    &.brx-option-active {
      color: var(--mac-btn-text-color-active);
      label.brx-option-active {
        span.brx-option-active.bricks-button {
          border-color: var(--mac-btn-border-color-active);
          background-color: var(--mac-btn-background-active);
          span.brx-option-count {
            color: var(--mac-btn-count-color-active);
          }
        }
      }

      // Item - Active - Hover
      &:hover {
        color: var(--mac-btn-text-color-active-hover);
        label.brx-option-active {
          span.brx-option-active.bricks-button {
            border-color: var(--mac-btn-border-color-active-hover);
            background-color: var(--mac-btn-background-active-hover);
            span.brx-option-count {
              color: var(--mac-btn-count-color-active-hover);
            }
          }
        }
      }
    }

    // Item - Disabled
    &:has(.brx-option-disabled) {
      opacity: var(--mac-btn-opacity-disabled);
      color: var(--mac-btn-text-color-disabled);
      label {
        span.brx-option-text.bricks-button {
          border-color: var(--mac-btn-border-color-disabled);
          background-color: var(--mac-btn-background-disabled);
          span.brx-option-count {
            color: var(--mac-btn-count-color-disabled);
          }
        }
      }

      // Item - Disabled - Hover
      &:hover {
        color: var(--mac-btn-text-color-disabled);
      }
    }
  }

  // Label
  label {
    transition: var(--mac-btn-transition);

    // Button
    span.brx-option-text.bricks-button {
      transition: var(--mac-btn-transition);
      padding: var(--mac-btn-padding);
      align-items: center;
      justify-content: center;
      gap: var(--mac-btn-gap);
      text-align: center;
      letter-spacing: 0;
      border-radius: var(--mac-btn-radius);
      border: var(--mac-btn-border);
      border-color: var(--mac-btn-border-color);
      background-color: var(--mac-btn-background);

      // Count
      span.brx-option-count {
        transition: var(--mac-btn-transition);
        opacity: var(--mac-count-opacity);
        color: var(--mac-btn-count-color);
      }
    }
  }
}



/// BRX Filter - Range
div.brxe-filter-range {
  @include filter-range-tokens;

  // Range
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 10px;

  // Range - Slider
  div.double-slider-wrap {
    padding-top: 0;
    width: 100%;
    display: flex;
    flex-direction: column;
    position: relative;

    // Slider Wrap
    div.slider-wrap {
      position: relative;

      // Slider Base
      div.slider-base {
        width: 100%;
        height: 0;
        position: absolute;
        left: 0;
        top: 50%;
        transform: translateY(-50%);
        border-radius: var(--mac-base-radius);
        border: var(--mac-base-height) solid var(--mac-base-color);
      }

      // Slider Track
      div.slider-track {
        height: 0;
        position:absolute;
        top: 50%;
        transform: translateY(-50%);
        border-radius: var(--mac-track-radius);
        border: var(--mac-track-thickness) solid var(--mac-track-color);
      }

      // Slider Input
      input.min, input.max {
        transition: var(--transition);
        appearance: none;
        -webkit-appearance: none;
        pointer-events: none;
        padding: 0;
        width: 100%;
        max-width: 100%;
        position: absolute;
        height: 0;
        border-radius: 2px;
        border: none;
        font-size: inherit;
        color: var(--mac-handle-color);
        line-height: 1;
        box-shadow: none;
      }
    }

    // Slider Value Group
    div.value-wrap {
      margin-top: var(--mac-value-space-top);
      display: flex;
      justify-content: space-between;

      // Slider Value Wrap
      span.lower, span.upper {
        display: inline-flex;
        gap: var(--mac-value-gap);

        // Slider Value
        span.value {
          padding: var(--mac-value-padding);
          font-size: var(--mac-value-font-size);
          color: var(--mac-value-text-color);
          font-weight: var(--mac-value-weight);
          border-radius: var(--mac-value-radius);
          background: var(--mac-value-background);
        }
      }
    }
  }

  // Range - Input
  div.min-max-wrap {
    display: flex;
    flex-direction: var(--mac-input-direction);
    gap: var(--mac-input-gap);

    // Block
    div.min-wrap, div.max-wrap {
      display: flex;
      gap: var(--mac-input-gap);
      align-items: center;
      flex-grow: 1;

      // Input
      input.min, input.max {
        @include filter-input;
      }
    }
  }
}



/// BRX Filter - Date
input.brxe-filter-datepicker.form-control {
  @include filter-input;

  // Active
  &.active {}
}



/// BRX Filter - Select
select.brxe-filter-select {
  @include filter-input;

  // Option
  option {
    align-items: center;
    color: var(--mac-option-text-color);
    background-color: var(--mac-option-background);

    // Option - Placeholder
    &.placeholder {
      opacity: 1;
      color: var(--mac-option-text-color);
    }

    // Option - Disabled
    &.brx-option-disabled {
      pointer-events: none;
      color: var(--mac-option-text-color-disabled);
      background-color: var(--mac-option-background-disabled);
    }
  }
}



/// BRX Filter - Search
div.brxe-filter-search {
  @include filter-input-tokens;

  // Input
  input {
    @include filter-input;

    // Placeholder
    &::placeholder {}
  }

  // Hover Icon Border
  &:hover {
    svg {
      color: var(--mac-input-text-color-hover);
      border-color: transparent;
    }
  }

  // Icon
  svg {
    transition: var(--mac-input-transition);
    opacity: 0.25;
    padding: 0 !important;
    width: var(--mac-input-line-height);
    height: var(--mac-input-line-height);
    font-size: var(--mac-input-line-height);
    color: var(--mac-input-text-color);
    border-radius: 0 var(--mac-input-radius) var(--mac-input-radius) 0;
    border: var(--mac-input-border);
    border-color: transparent;
    background-color: transparent;

    // Icon - Hover
    &:hover {
      opacity: 1;
      color: var(--danger);
      background-color: transparent;
      border-color: transparent;
    }
  }
}



/// BRX Filter - Submit
button.brxe-filter-submit {
  @include mac-btn;

  // Overrides
  --btn-font-size: var(--text-s);

  // Button
  transition: none;
}



/// BRX Filter - Active
ul.brxe-filter-active-filters {

  // Tokens
  --mac-list-direction: row;
  --mac-item-gap: 1ex;

  // List
  flex-direction: var(--mac-list-direction);
  gap: var(--mac-item-gap);

  // Item
  li {

    // Button
    button {
      @include filter-button-tokens;
      @include mac-btn;

      // Button Overrides
      --mac-btn-gap: 0.5ex;

      --btn-padding-block: var(--mac-btn-padding-block);
      --btn-padding-inline: var(--mac-btn-padding-inline);
      --btn-font-size: var(--text-s);
      --btn-font-weight: 400;

      --btn-text-color: var(--mac-btn-text-color);
      --btn-background: var(--mac-btn-background);
      --btn-border-color: var(--mac-btn-border-color);

      --btn-text-color-hover: var(--danger-ultra-light);
      --btn-background-hover: var(--danger);
      --btn-border-color-hover: var(--danger);

      transition: var(--mac-btn-transition);
      min-width: auto;
      gap: var(--mac-btn-gap);
      color: var(--btn-text-color) !important;
      border-radius: var(--mac-btn-radius);
      border: var(--mac-btn-border);
      border-color: var(--btn-border-color);
      background-color: var(--btn-background);

      // Button - Hover
      &:hover {
        color: var(--btn-text-color-hover) !important;
        background-color: var(--btn-background-hover);
      }

      // Icon
      svg.icon {
        height: 1em;
      }
    }
  }
}



/// BRX Filter - Load
.brxe-button[data-interactions*="loadMore"][class*="btn--"] {
  @include mac-load;

  // Loading
  &.is-loading {
    cursor: wait;
    opacity: 0.5;
    filter: grayscale(1);
  }

  // Nothing left to load
  &.brx-load-more-hidden {
    display: none;
  }
}



/// --- FACETS ---

/// WPGB Facet - Utilities
// Variables
@mixin facet-tokens {
  // Overrides
  --wpgb-accent-scheme-1: var(--primary);
  --wpgb-dark-scheme-1: var(--neutral);
  --wpgb-dark-scheme-2: var(--neutral-semi-dark);
  --wpgb-dark-scheme-3: var(--neutral-semi-light);
  --wpgb-light-scheme-1: var(--white);
  --wpgb-light-scheme-2: var(--neutral-ultra-light);
  --wpgb-light-scheme-3: var(--neutral-light);
  --wpgb-grey-100: var(--white);
  --wpgb-grey-200: var(--neutral-ultra-light);
  --wpgb-grey-500: var(--neutral-light);
  --wpgb-grey-700: var(--neutral-semi-light);
  --wpgb-grey-800: var(--neutral-semi-dark);
  --wpgb-grey-900: var(--neutral);
  --wpgb-red: var(--danger);
  --wpgb-border-radius: var(--radius);
  --wpgb-border-width: 1px;

  // Base Settings
  --mac-facet-font: var(--mac-ff-body);
  --mac-facet-font-size: var(--text-s);
  --mac-facet-font-size-s: var(--text-xs);
  --mac-facet-font-weight: 400;
  --mac-facet-line-height: 1;
  --mac-facet-border: var(--border);
  --mac-facet-radius: var(--btn-radius);
  --mac-facet-transition: all var(--transition-duration) var(--transition-timing);

  // Dimension Settings
  --mac-facet-min-height: 1em;
  --mac-facet-width: auto;
  --mac-facet-select-width: 100%;
  --mac-facet-margin: 0;

  // Spacing
  --mac-facet-gap: calc(var(--card-gap, var(--fr-card-gap)) * 0.5);
  --mac-facet-padding-block: calc(var(--btn-padding-block) * 0.75);
  --mac-facet-padding-inline: calc(var(--btn-padding-inline) * 0.75);
  --mac-facet-padding: var(--mac-facet-padding-block) var(--mac-facet-padding-inline);
  --mac-facet-controls-margin: 0;
  --mac-facet-controls-padding: 0;

  // Option Defaults
  --mac-facet-option-text-color: var(--mac-facet-text-color);
  --mac-facet-option-background: var(--mac-facet-background);

  // Toggle Settings
  --mac-facet-toggle-padding-block: calc(var(--btn-padding-block) * 0.25);
  --mac-facet-toggle-padding-inline: calc(var(--btn-padding-inline) * 0.25);
  --mac-facet-toggle-padding: var(--mac-facet-toggle-padding-block) var(--mac-facet-toggle-padding-inline);
  --mac-facet-toggle-margin: 0;
  --mac-facet-toggle-color: var(--mac-facet-text-color);
  --mac-facet-toggle-width: 2em;
  --mac-facet-toggle-height: 100%;
  --mac-facet-toggle-stroke-width: 1.5px;

  // Separator Settings
  --mac-facet-separator-color: var(--mac-facet-text-color);
  --mac-facet-separator-opacity: 0.5;
  --mac-facet-separator-width: 1px;

  // Focus
  --mac-facet-focus-color: var(--focus-color);

  // Color Settings
  // Base Colors
  --mac-facet-background: var(--base);
  --mac-facet-text-color: var(--neutral-ultra-light);
  --mac-facet-border-color: var(--base);

  --mac-facet-border-color-light: var(--neutral-semi-light);

  // Hover Colors
  --mac-facet-background-hover: var(--base-dark);
  --mac-facet-text-color-hover: var(--base-ultra-light);
  --mac-facet-border-color-hover: var(--base-dark);

  // Active Colors
  --mac-facet-background-active: var(--primary);
  --mac-facet-text-color-active: var(--primary-ultra-light);
  --mac-facet-border-color-active: var(--primary);

  // Active Hover Colors
  --mac-facet-background-active-hover: var(--primary-hover);
  --mac-facet-text-color-active-hover: var(--primary-light);
  --mac-facet-border-color-active-hover: var(--primary-hover);
}

div.brxe-wpgb-facet {
  @include facet-tokens;
}

@mixin mac-facet-btn {
  @include mac-btn;
  --btn-background: var(--mac-facet-background);
  --btn-background-hover: var(--mac-facet-background-hover);
  --btn-text-color: var(--mac-facet-text-color);
  --btn-text-color-hover: var(--mac-facet-text-color-hover);
  --btn-border-color: var(--mac-facet-border-color);
  --btn-border-color-hover: var(--mac-facet-border-color-hover);
  --btn-outline-background-hover: var(--mac-facet-background-hover);
  --btn-outline-border-hover: var(--mac-facet-border-color-hover);
  --btn-outline-text-color: var(--mac-facet-border-color);
  --btn-outline-text-color-hover: var(--mac-facet-color-hover);
  --focus-color: var(--mac-facet-focus-color);

  transition: var(--mac-facet-transition);
  margin: var(--mac-facet-margin);
  font-size: var(--mac-facet-font-size, var(--btn-font-size));
  text-align: center;
  justify-content: center;
  align-items: center;
  height: auto;
  width: auto;
}

@mixin mac-facet-btn--danger {
  @include mac-facet-btn;
  --btn-background: var(--danger);
  --btn-background-hover: var(--danger-hover);
  --btn-text-color: var(--white);
  --btn-text-color-hover: var(--danger-ultra-light);
  --btn-border-color: var(--danger);
  --btn-border-color-hover: var(--danger-hover);
  --btn-outline-background-hover: var(--danger-hover);
  --btn-outline-border-hover: var(--danger-hover);
  --btn-outline-text-color: var(--danger);
  --btn-outline-text-color-hover: var(--danger-ultra-light);
  --focus-color: var(--danger-dark);
}




/// WPGB Facet - Sort
div.brxe-wpgb-facet:has(.wpgb-sort-facet) {
  font-family: var(--mac-facet-font);
  font-size: var(--mac-facet-font-size);

  &:not(:has(div.wpgb-sort-facet)) {
  	display: none;
  }

  div.wpgb-facet {

    fieldset {

      &:last-child {
        margin: var(--mac-facet-margin);
      }

      div.wpgb-sort-facet {

        label {
          width: var(--mac-facet-width);
          min-width: 14em;

          select.wpgb-select {
            transition: var(--mac-facet-transition);
            width: var(--mac-facet-select-width);
            background: var(--mac-facet-background);
            border-radius: var(--mac-facet-radius);
            border: var(--mac-facet-border);
            font-size: var(--mac-facet-font-size);
            line-height: var(--mac-facet-line-height);
            min-height: var(--mac-facet-min-height);
            padding: var(--mac-facet-padding);
            color: var(--mac-facet-text-color);

            &:hover {
              background: var(--mac-facet-background-hover);
              color: var(--mac-facet-text-color-hover);
              border-color: var(--mac-facet-border-color-hover);
            }

            option {
              color: var(--mac-facet-option-text-color);
              background: var(--mac-facet-option-background);
            }
          }

          span.wpgb-select-controls {
            margin: var(--mac-facet-controls-margin);
            padding: var(--mac-facet-controls-padding);

            span.wpgb-select-separator {
              background: var(--mac-facet-separator-color);
