/// WPGB Facet - Pages
div.brxe-wpgb-facet:has(.wpgb-pagination) {

  &:not(:has(li.wpgb-page)) {
  	display: none;
  }

  nav.wpgb-pagination-facet {

    ul.wpgb-pagination {
      display: flex;
      flex-direction: row;
      flex-wrap: wrap;
      gap: var(--mac-facet-gap);

      li.wpgb-page {
        min-width: 0;
        width: var(--mac-facet-width);
        line-height: var(--mac-facet-line-height);
        font-size: var(--mac-facet-font-size);
        height: var(--mac-facet-toggle-height);
        margin: var(--mac-facet-toggle-margin);

        a {
          white-space: nowrap;
          font-size: var(--mac-facet-font-size);
          line-height: var(--mac-facet-line-height);
          padding-block: calc(var(--mac-facet-padding-block) * 0.75);
          padding-inline: calc(var(--mac-facet-padding-inline) * 0.75);
          color: var(--mac-facet-text-color);
          background: var(--mac-facet-background);
          transition: var(--mac-facet-transition);
          border-radius: var(--btn-border-radius);
          border: var(--mac-facet-border);
          border-color: var(--mac-facet-border-color);
          min-width: var(--mac-facet-toggle-width);

          &:hover {
            background: var(--mac-facet-background-hover);
            color: var(--mac-facet-text-color-hover);
            border-color: var(--mac-facet-border-color-hover);
          }

          &[aria-current="true"] {
            background: var(--mac-facet-background-active);
            color: var(--mac-facet-text-color-active);
            border-color: var(--mac-facet-border-color-active);

            &:hover {
              background: var(--mac-facet-background-active-hover);
              color: var(--mac-facet-text-color-active-hover);
              border-color: var(--mac-facet-border-color-active-hover);
            }
          }
        }

        &.wpgb-page-prev, &.wpgb-page-next {

          a {
            background: var(--mac-facet-background);
          	color: var(--mac-facet-text-color);
          	border-color: var(--mac-facet-border-color);

          		&:hover {
            	background: var(--mac-facet-background-hover);
            	color: var(--mac-facet-text-color-hover);
            	border-color: var(--mac-facet-border-color-hover);
          	}
          }
        }

        &.wpgb-page-prev {}

        &.wpgb-page-next {}

        span.wpgb-dots-page {
          color: var(--mac-facet-background);
          line-height: var(--mac-facet-line-height);
          padding-block: 1ex;
          opacity: var(--mac-facet-separator-opacity);
          font-size: var(--mac-facet-font-size);
        }
      }
    }
  }
}



