/// WPGB Facet - Checkbox
div.brxe-wpgb-facet:has(.wpgb-checkbox-facet) {
  --mac-font-size: calc(var(--mac-facet-font-size) * 0.9);
  font-family: var(--mac-facet-font);
  font-size: var(--mac-font-size);

  &:not(:has(ul.wpgb-hierarchical-list li)) {
  	display: none;
  }

  div.wpgb-facet {

    fieldset {

      &:last-child {
        margin: var(--mac-facet-margin);
      }

      div.wpgb-checkbox-facet {

        > ul.wpgb-hierarchical-list {
          display: flex;
          flex-direction: column;
        }

        ul.wpgb-hierarchical-list {
          gap: var(--mac-facet-gap);

          li {
            margin: var(--mac-facet-margin);
            display: flex;
            flex-direction: column;
            gap: var(--mac-facet-gap);

            [aria-expanded="true"] {

              ul.wpgb-hierarchical-list {
                display: flex;
                flex-direction: column;
              }
            }

            &[aria-selected="true"] {

              > div.wpgb-checkbox {

                span.wpgb-checkbox-control {
                  background: var(--mac-facet-background-active);
                  border-color: var(--mac-facet-border-color-active);
                }
              }
            }

            div.wpgb-checkbox {
              line-height: var(--mac-facet-line-height);

              &[aria-pressed="mixed"] {

                span.wpgb-checkbox-control {
                  border-color: var(--mac-facet-border-color-active);

                  &::before {
                    border-color: var(--mac-facet-border-color-active);
                  }
                }  
              }

              span.wpgb-checkbox-control {
                align-items: center;
                height: 1.15em;
                width: 1.15em;
                border: var(--mac-facet-border);
                border-color: var(--mac-facet-border-color-light);
                transition: var(--mac-facet-transition);
                background: var(--mac-facet-text-color);

                &::before {
                  transition: var(--mac-facet-transition);
                }

                &::after {
                  transition: var(--mac-facet-transition);
                }
              }

              span.wpgb-checkbox-label {
                transition: var(--mac-facet-transition);
                font-size: var(--mac-font-size);
                color: var(--mac-facet-background);

                span {
                  opacity: var(--mac-facet-separator-opacity);
                }
              }
            }
          }
        }
      }
    }
  }
}



/// WPGB Facet - Radio
div.brxe-wpgb-facet:has(.wpgb-radio-facet) {
  font-family: var(--mac-facet-font);
  font-size: var(--mac-facet-font-size);

  &:not(:has(ul.wpgb-hierarchical-list li)) {
  	display: none;
  }

  div.wpgb-facet {

    fieldset {

      &:last-child {
        margin: var(--mac-facet-margin);
      }

      div.wpgb-radio-facet {

        ul.wpgb-hierarchical-list {
          display: flex;
          flex-direction: column;
          gap: var(--mac-facet-gap);

          li {
            margin: var(--mac-facet-margin);

            div.wpgb-radio {

              &[aria-pressed="true"] {

                span.wpgb-radio-control {
                  border-color: var(--mac-facet-border-color-active);
                }
              }

              span.wpgb-radio-control {
                transition: var(--mac-facet-transition);
                background: var(--mac-facet-text-color);
                border-color: var(--mac-facet-border-color-light);
                border-width: 1px;
                height: 1em;
                width: 1em;

                &::after {
                  transition: var(--mac-facet-transition);
                  background-color: var(--mac-facet-background-active);
                  border: 0 solid var(--mac-facet-text-color);
                  min-height: 100%;
                  min-width: 100%;
                  max-height: 100%;
                  max-width: 100%;
                  scale: 0.6;
                }
              }

              span.wpgb-radio-label {
                transition: var(--mac-facet-transition);
                font-size: var(--mac-facet-font-size-s);

                div.wpgb-radio:hover & {
                  color: var(--mac-facet-background-active);
                }

                span {
                  opacity: var(--mac-facet-separator-opacity);
                }
              }
            }
          }
        }
      }
    }
  }
}



