              --btn-background: var(--mac-facet-text-color);
              --btn-background-hover: var(--mac-facet-background-hover);
              --btn-text-color: var(--mac-facet-background);
              --btn-text-color-hover: var(--mac-facet-text-color-hover);
              --btn-border-color: var(--mac-facet-text-color);
              --btn-border-color-hover: var(--mac-facet-background-hover);
              padding-block: calc(var(--btn-padding-block) * 0.2);
              padding-inline: calc(var(--btn-padding-inline) * 0.2);
              font-size: var(--mac-facet-font-size-s, var(--btn-font-size));
              border-width: 1px;
              min-width: auto;

              &[aria-pressed="true"] {
                background: var(--mac-facet-background-active);
                color: var(--mac-facet-text-color-active);
                border-color: var(--mac-facet-border-color-active);

                &:hover {
                  background: var(--mac-facet-background-active-hover);
                  color: var(--mac-facet-text-color-active-hover);
                  border-color: var(--mac-facet-border-color-active-hover);
                }
              }

              span.wpgb-button-label {

                span {
                  opacity: var(--mac-facet-separator-opacity);
                }
              }
            }
          }
        }
      }
