/// WPGB Facet - Rating
div.brxe-wpgb-facet:has(.wpgb-rating-facet) {
  --mac-star-size: 1.25em;
  --mac-star-color: var(--neutral-semi-light);
  --mac-stroke-width: 1.5;

  --mac-label-color: var(--neutral-dark);
  --mac-count-color: currentColor;
  --mac-count-opacity: 0.5;

  --mac-back-icon-size: 1em;
  --mac-back-icon-scale: 0.75;
  --mac-back-icon-stroke-width: 4px;
  --mac-back-icon-color: currentColor;

  font-family: var(--mac-facet-font);
  font-size: var(--mac-facet-font-size);

  &:not(:has(ul.wpgb-hierarchical-list li)) {
  	display: none;
  }

  div.wpgb-facet {
    transition: var(--mac-facet-transition);

    fieldset {

      &:last-child {
        margin: var(--mac-facet-margin);
      }

      legend.wpgb-facet-title {
        font-size: var(--mac-facet-font-size);
        line-height: var(--mac-facet-line-height);
        margin: var(--mac-facet-margin);
        padding: 0;
      }

      div.wpgb-rating-facet {

        ul.wpgb-hierarchical-list {
          display: flex;
          flex-direction: column;
          gap: var(--mac-facet-gap);

          li {
            display: flex;
            margin: var(--mac-facet-margin);

            &:hover {

              div.wpgb-rating {

                span.wpgb-rating-control {

                  span.wpgb-rating-svg {
                    color: var(--mac-star-color-active);
                  }
                }

                span.wpgb-rating-label {}
              }
            }

            &:has(.wpgb-rating-reset) {

              div.wpgb-rating-reset {

                span.wpgb-rating-control {

                  svg {
                    margin: 0;
                    height: var(--mac-back-icon-size);
                    width: var(--mac-back-icon-size);
                    stroke-width: var(--mac-back-icon-stroke-width);
                    color: var(--mac-back-icon-color);
                    scale: var(--mac-back-icon-scale);

                    use {}
                  }
                }

                span.wpgb-rating-label {}
              }
            }

            div.wpgb-rating {
              display: flex;
              gap: var(--mac-facet-gap);

              &[aria-pressed="true"] {

                span.wpgb-rating-control {

                  span.wpgb-rating-svg {}
                }
              }

              span.wpgb-rating-control {
                transition: var(--mac-facet-transition);
                color: var(--mac-star-color);
                height: var(--mac-star-size);
                width: auto;

                svg.wpgb-rating-svg {
                  stroke-width: var(--mac-stroke-width);
                  transition: var(--mac-facet-transition);

                  use#wpgb-rating-stars-svg {

                    &:first-child {}

                    &:last-child {}
                  }
                }
              }

              span.wpgb-rating-label {
                padding: 0;
                font-size: var(--mac-facet-font-size);
                font-weight: var(--mac-facet-font-weight);
                line-height: var(--mac-facet-line-height);
                margin: 0;
                color: var(--mac-label-color);

                span {
                  color: var(--mac-count-color);
                  opacity: var(--mac-count-opacity);
                }
              }
            }
          }
        }
      }
    }
  }
}



/// WPGB Facet - Color
div.brxe-wpgb-facet:has(.wpgb-color-facet) {
  --mac-color-size: 1.5em;
  // Label
  --mac-label-color: var(--white);
  --mac-label-font-size: calc(var(--mac-facet-font-size-s) * 0.85);
  --mac-label-padding: 0.1em 0.5em;
  // Count
  --mac-count-color: currentColor;
  --mac-count-opacity: 0.75;

  font-family: var(--mac-facet-font);
  font-size: var(--mac-facet-font-size);

  &:not(:has(ul.wpgb-inline-list li)) {
  	display: none;
  }

  div.wpgb-facet {
    transition: var(--mac-facet-transition);

    fieldset {
      margin-inline: 0;
      padding-block: 0;
      padding-inline: 0;

      &:last-child {
        margin: var(--mac-facet-margin);
      }

      legend.wpgb-facet-title {
        font-size: var(--mac-facet-font-size);
        line-height: var(--mac-facet-line-height);
        margin: var(--mac-facet-margin);
        padding: 0;
      }

      div.wpgb-color-facet {

        ul.wpgb-inline-list {
          margin: 0;
          gap: var(--mac-facet-gap);

          li {
            margin: 0;

            div.wpgb-color {

              &[aria-pressed="true"] {}

              span.wpgb-color-control {
                height: var(--mac-color-size);
                width: var(--mac-color-size);
                transition: var(--mac-facet-transition);

                &::before {}

                &::after {}
              }

              span.wpgb-color-label {
                color: var(--mac-label-color);
                font-size: var(--mac-label-font-size);
                padding: var(--mac-label-padding);
                top: -2.5em;
                left: 50%;
                transform: translate3d(-50%, 0, 0);

                &::after {}

                span {
                  color: var(--mac-count-color);
                  opacity: var(--mac-count-opacity);
                }
              }
            }
          }
        }
      }
    }
  }
}



/// WPGB Facet - Index
div.brxe-wpgb-facet:has(.wpgb-az-index-facet) {
  --mac-label-color: var(--mac-facet-background);
  --mac-label-color-active: var(--primary);
  --mac-label-weight-active: 700;
  --mac-count-color: currentColor;
  --mac-count-opacity: 0.5;

  font-family: var(--mac-facet-font);
  font-size: var(--mac-facet-font-size);

  &:not(:has(ul.wpgb-inline-list li)) {
  	display: none;
  }

  div.wpgb-facet {
    transition: var(--mac-facet-transition);

    fieldset {

      &:last-child {
        margin: var(--mac-facet-margin);
      }

      legend.wpgb-facet-title {
        font-size: var(--mac-facet-font-size);
        line-height: var(--mac-facet-line-height);
        margin: var(--mac-facet-margin);
        padding: 0;
      }

      div.wpgb-az-index-facet {

        ul.wpgb-inline-list {
          margin: 0;
          gap: var(--mac-facet-gap);

          li {
            margin: 0;

            div.wpgb-az-index {

              &[aria-pressed="true"] {

                span.wpgb-az-index-label {
                  color: var(--mac-label-color-active);
                  font-weight: var(--mac-label-weight-active);

                  span {}
                }
              }

              span.wpgb-az-index-label {
                transition: var(--mac-facet-transition);
                font-size: var(--mac-facet-font-size);
                line-height: var(--mac-facet-line-height);
                color: var(--mac-label-color);

                &:hover {
                  color: var(--mac-label-color-active);
                }

                span {
                  color: var(--mac-count-color);
                  opacity: var(--mac-count-opacity);
                }
              }
            }
          }
        }
      }
    }
  }
}



/// WPGB Facet - Search
div.brxe-wpgb-facet:has(.wpgb-search-facet) {
  --mac-icon-size: 1em;
  --mac-icon-color: var(--mac-facet-background);
  --mac-icon-stroke-width: 3;
  --mac-icon-opacity: 0.25;

  --mac-input-padding-inline: 1em;
  --mac-input-padding-block: 0.35em;

  --mac-clear-icon-size: 1.25em;
  --mac-clear-icon-stroke-width: 2.5;
  --mac-clear-icon-color: var(--mac-facet-background);
  --mac-clear-icon-opacity: var(--mac-icon-opacity);

  font-family: var(--mac-facet-font);
  font-size: var(--mac-facet-font-size);

  &:not(:has(input.wpgb-input)) {
  	display: none;
  }

  div.wpgb-facet {
    transition: var(--mac-facet-transition);

    fieldset {

      &:last-child {
        margin: var(--mac-facet-margin);
      }

      legend.wpgb-facet-title {
        font-size: var(--mac-facet-font-size);
        line-height: var(--mac-facet-line-height);
        margin: var(--mac-facet-margin);
        padding: 0;
      }

      div.wpgb-search-facet {

        label {

          input.wpgb-input {
            transition: var(--mac-facet-transition);
            padding-inline: var(--mac-input-padding-inline);
            padding-block: var(--mac-input-padding-block);
            text-indent: calc(var(--mac-icon-size) * 0.75);
            border: var(--border);
            border-radius: var(--mac-facet-radius);
            border-color: var(--mac-facet-border-color);
            font-size: var(--mac-facet-font-size);
            line-height: var(--mac-facet-line-height);
            min-height: var(--mac-facet-line-height);
            color: var(--mac-facet-background);

            &::placeholder {
              color: currentColor;
              opacity: 0.5;
            }
          }

          svg.wpgb-input-icon {
            margin: 0;
            height: var(--mac-icon-size);
            width: var(--mac-icon-size);
            top: calc(50% - calc(var(--mac-icon-size) * 0.5));
            left: calc(var(--mac-icon-size) * 0.5);
            stroke: var(--mac-icon-color);
            stroke-width: var(--mac-icon-stroke-width);
            opacity: var(--mac-icon-opacity);
          }
        }

        button.wpgb-clear-button {
          transition: none;
          margin: 0;
          padding: 0 calc(var(--mac-input-padding-inline) * 0.5);

          &:hover {

            svg {
              stroke: var(--danger-hover);
              opacity: 1;
            }
          }

          svg {
            transition: none;
            height: var(--mac-clear-icon-size);
            width: var(--mac-clear-icon-size);
            stroke-width: var(--mac-clear-icon-stroke-width);
            stroke: var(--mac-clear-icon-color);
            opacity: var(--mac-clear-icon-opacity);
          }
        }
      }
    }
  }
}



/// WPGB Facet - Complete
div.brxe-wpgb-facet:has(.wpgb-autocomplete-facet) {
  --mac-icon-size: 1em;
  --mac-icon-color: var(--mac-facet-background);
  --mac-icon-stroke-width: 3;
  --mac-icon-opacity: 0.25;

  --mac-input-padding-inline: 1em;
  --mac-input-padding-block: 0.35em;

  --mac-clear-icon-size: 1.25em;
  --mac-clear-icon-stroke-width: 2.5;
  --mac-clear-icon-color: var(--mac-facet-background);
  --mac-clear-icon-opacity: var(--mac-icon-opacity);

  --mac-loader-color: var(--mac-facet-background);

  font-family: var(--mac-facet-font);
  font-size: var(--mac-facet-font-size);

  &:not(:has(input.wpgb-input)) {
  	display: none;
  }

  div.wpgb-facet {
    transition: var(--mac-facet-transition);

    fieldset {

      &:last-child {
        margin: var(--mac-facet-margin);
      }

      legend.wpgb-facet-title {
        font-size: var(--mac-facet-font-size);
        line-height: var(--mac-facet-line-height);
        margin: var(--mac-facet-margin);
        padding: 0;
      }

      div.wpgb-autocomplete-facet {

        label {

          div.acplt {

            span.acplt-message {}

            input.wpgb-input {
              transition: var(--mac-facet-transition);
              padding-inline: var(--mac-input-padding-inline);
              padding-block: var(--mac-input-padding-block);
              text-indent: calc(var(--mac-icon-size) * 0.75);
              border: var(--border);
              border-radius: var(--mac-facet-radius);
              border-color: var(--mac-facet-border-color);
              font-size: var(--mac-facet-font-size);
              line-height: var(--mac-facet-line-height);
              min-height: var(--mac-facet-line-height);
              color: var(--mac-facet-background);

              &::placeholder {
                color: currentColor;
                opacity: 0.5;
              }
            }

            button.acplt-clear {
              transition: none;
              margin: 0;
              padding: 0 calc(var(--mac-input-padding-inline) * 0.5);
              width: auto;

              &:hover {

                svg {
                  stroke: var(--danger-hover);
                  opacity: 1;
                }
              }

              svg {
                transition: none;
                height: var(--mac-clear-icon-size);
                width: var(--mac-clear-icon-size);
                stroke-width: var(--mac-clear-icon-stroke-width);
                stroke: var(--mac-clear-icon-color);
                opacity: var(--mac-clear-icon-opacity);
              }
            }

            .acplt-loader {
              margin: 0;
              padding: 0;
              width: calc(var(--mac-clear-icon-size) + calc(var(--mac-input-padding-inline) * 0.5)) !important;

              span {
                background: var(--mac-loader-color);
              }
            }
          }

          svg.wpgb-input-icon {
            margin: 0;
            height: var(--mac-icon-size);
            width: var(--mac-icon-size);
            top: calc(50% - calc(var(--mac-icon-size) * 0.5));
            left: calc(var(--mac-icon-size) * 0.5);
            stroke: var(--mac-icon-color);
            stroke-width: var(--mac-icon-stroke-width);
            opacity: var(--mac-icon-opacity);
          }
        }
      }
    }
  }
}



/// WPGB Facet - Complete - ACPLT
.acplt-menu {
  background: var(--white) !important;
  border-radius: var(--mac-facet-radius) !important;
  box-shadow: var(--box-shadow-m) !important;
  margin: 0.5em 0 !important;
}

.acplt-list .acplt-item {
  color: var(--text-color) !important;
  font-size: 0.85em !important;
  line-height: var(--text-line-height) !important;
  padding: calc(var(--btn-padding-block) * 0.5) calc(var(--btn-padding-inline) * 0.5) !important;

  mark {
    background-color: var(--primary) !important;
    color: var(--white) !important;
  }
}

.acplt-list .acplt-item[aria-selected=true] {
  background-color: var(--primary-ultra-light) !important;
}

.acplt-list .acplt-item[aria-disabled=true], .acplt-list .acplt-item[aria-disabled=true] mark {
  background-color: transparent !important;
  color: var(--text-color) !important;
  opacity: 0.5 !important;
  pointer-events: none !important;
  user-select: none !important;
}



/// WPGB Facet - Selected
div.brxe-wpgb-facet:has(.wpgb-selection-facet) {
  --mac-btn-gap: 1ex;
  --mac-btn-padding-block: 0.25em;
  --mac-btn-padding-inline: 0.5em;

  --mac-icon-size: 0.5em;
  --mac-icon-stroke-width: 2px;
  --mac-icon-color: currentColor;
  --mac-icon-opacity: 1;

  font-family: var(--mac-facet-font);
  font-size: var(--mac-facet-font-size);

  &:not(:has(ul.wpgb-inline-list li)) {
  	display: none;
  }

  div.wpgb-facet {
    transition: var(--mac-facet-transition);

    fieldset {

      &:last-child {
        margin: var(--mac-facet-margin);
      }

      legend.wpgb-facet-title {
        font-size: var(--mac-facet-font-size);
        line-height: var(--mac-facet-line-height);
        margin: var(--mac-facet-margin);
        padding: 0;
      }

      div.wpgb-selection-facet {

        ul.wpgb-inline-list {
          margin: 0;
          gap: var(--mac-facet-gap);

          li {
            margin: 0;

            div.wpgb-button {
              @include mac-facet-btn--danger;
              --btn-padding-block: var(--mac-btn-padding-block);
              --btn-padding-inline: var(--mac-btn-padding-inline);
              --btn-min-width: auto;

              white-space: nowrap;
              gap: var(--mac-btn-gap);

              span.wpgb-button-control {
                height: var(--mac-icon-size);
                width: var(--mac-icon-size);

                &::before, &::after {
                  height: var(--mac-icon-size);
                  width: var(--mac-icon-stroke-width);
                  left: calc(var(--mac-icon-size) * 0.5);
                  opacity: var(--mac-icon-opacity);
                  background: var(--mac-icon-color);
                }
              }
              span.wpgb-button-label {
                line-height: var(--mac-facet-line-height);
                padding: 0;
                font-size: var(--mac-btn-font-size);
                line-height: var(--mac-facet-line-height);
              }
            }
          }
        }
      }
    }
  }
}



/// --- MEC ---

/// MEC - Mixins
@mixin mac-mec-btn-style {
  --btn-background: var(--primary);
  --btn-background-hover: var(--primary-hover);
  --btn-text-color: var(--primary-ultra-light);
  --btn-text-color-hover: var(--primary-ultra-light);
  --btn-border-color: var(--primary);
  --btn-border-color-hover: var(--primary-hover);
  --btn-outline-background-hover: var(--primary-hover);
  --btn-outline-border-hover: var(--primary-hover);
  --btn-outline-text-color: var(--primary);
  --btn-outline-text-color-hover: var(--primary-ultra-light);
  --focus-color: var(--primary-dark);
  padding-block: var(--btn-padding-block);
  padding-inline: var(--btn-padding-inline);
  min-inline-size: var(--btn-min-width);
  line-height: var(--btn-line-height);
  font-size: var(--btn-font-size, var(--text-m));
  font-weight: var(--btn-font-weight);
  font-style: var(--btn-font-style);
  text-decoration: var(--btn-text-decoration);
  text-transform: var(--btn-text-transform);
  letter-spacing: var(--btn-letter-spacing);
  border-width: var(--btn-border-width);
  border-style: var(--btn-border-style);
  border-radius: var(--btn-border-radius);
  display: var(--btn-display, inline-flex);
  text-align: center;
  justify-content: center;
  align-items: center;
  background: var(--btn-background);
  color: var(--btn-text-color);
  border-color: var(--btn-border-color);
  transition: var(--transition);

  &:hover {
    background: var(--btn-background-hover);
    border-color: var(--btn-border-color-hover);
    color: var(--btn-text-color-hover);
    text-decoration: var(--btn-text-decoration-hover);
  }
}

/// MEC - Archive
// (Skin = List View, Style = Modern)
section#main-content.mec-container article,
section.mac-mec-events-list article,
section.mac-mec-events-list div.mec-load-more-button {
  margin-inline: auto !important;
}

section.mac-mec-events-list,
section#main-content.mec-container {
  // background-color: var(--primary-ultra-light);

  // Utilities
  div.mec-wrap {
    font-size: var(--text-m);
    color: var(--text-color);
    line-height: var(--text-line-height);
    font-weight: 400;
  }

  div.col-md-2.col-sm-2,
  div.col-md-6.col-sm-6,
  div.col-md-4.col-sm-4.mec-btn-wrapper {
    padding-left: 0;
    padding-right: 0;
    position: static;
    min-height: initial;
    width: auto;

    @media (min-width: 768px) {
      padding: 0;
      position: static;
      left: unset;
      top: unset;
      width: auto;
    }

    @media (min-width: 769px) {
      width: auto;
      float: unset;
    }

    @media (min-width: 961px) {
      width: auto;
      float: unset;
    }

    @media (min-width: 1200px) {
      padding-left: 0;
      padding-right: 0;
    }
  }

  // Elements
  // div.mac-mec-events-list__inner {

  //   div.mac-mec-events-list__shortcode {

  div.mec-wrap.mec-skin-list-container {
    width: 100%;
    display: flex;
    flex-direction: column;
    gap: var(--grid-gap);

    div.mec-skin-list-events-container {
      width: 100%;
      display: flex;
      flex-direction: column;
      gap: var(--grid-gap);

      div.mec-event-list-modern {
        display: grid;
        grid-template-columns: var(--grid-1);
        gap: var(--grid-gap);

        div.mec-month-divider {
          margin: 0;
          margin-block: var(--fr-card-gap);
          display: flex;
          flex-direction: row;
          gap: var(--fr-card-gap);
          align-items: center;
          text-align: center;
          grid-column: var(--mac-month-col-span);

          &:nth-child(2) {
            margin-block-start: 0;
          }

          &::before,
          &::after {
            content: '';
            height: 0.1em;
            flex: 1;
            background: var(--primary-light);
          }

          &::before {
          }

          &::after {
          }

