  --mac-font-weight: 400;
  --mac-font-weight-bold: 700;
  --mac-line-height-tight: 1;
  --mac-letter-spacing-wide: 0.1em;
  --mac-text-transform: uppercase;

  --mac-cell-min-height: 8em;
  --mac-today-cell-accent-width: 0.15em;
  --mac-today-cell-accent-color: var(--mac-text-color-accent);

  --mac-bg-default: var(--white);
  --mac-month-link-bg-hover: var(--base-hover);
  --mac-bg-disabled: var(--neutral-ultra-light);
  --mac-bg-accent-light: var(--primary-ultra-light);
  --mac-bg-header: var(--primary-light);

  --mac-text-color-default: var(--text-color);
  --mac-text-color-disabled: var(--neutral-semi-light);
  --mac-text-color-heading: var(--primary-ultra-dark);
  --mac-text-color-dark: var(--primary-dark);
  --mac-text-color-month-link: var(--base);
  --mac-text-color-month-link-hover: var(--base-ultra-light);
  --mac-text-color-accent: var(--primary);
  --mac-text-color-accent-hover: var(--primary-hover);
  --mac-event-title-color: var(--mac-event-title-color);

  .mac-mec-events-calendar__inner {
    overflow-x: auto;

    .mac-mec-events-calendar__shortcode {
      .mec-wrap {
        font-weight: var(--mac-font-weight);
        color: var(--mac-text-color-default);
        font-size: var(--text-m);
        line-height: var(--text-line-height);

        .mec-calendar {
          border: none;
          width: 100%;
          height: 100%;
          box-shadow: none;
          border-radius: 0;

          .mec-calendar-side {
            display: block;
            border: none;
            padding: 0;
            width: 100%;
            height: 100%;
            box-shadow: none;
            text-align: center;

            &::before,
            &::after {
              content: ' ';
              display: table;
            }

            &::after {
              clear: both;
            }

            .mec-skin-monthly-view-month-navigator-container {
              .mec-month-navigator {
                @media (max-width: 768px) {
                  margin-bottom: var(--fr-card-gap);
                  width: 100%;
                  max-width: calc(100vw - calc(var(--gutter) * 2));
                  display: flex;
                  flex-direction: column;
                  gap: var(--fr-card-gap);
                }

                .mec-load-month,
                .mec-previous-month,
                .mec-next-month {
                  border-radius: var(--radius);
                  border: var(--border);
                  line-height: var(--mac-line-height-tight);
                  z-index: 1;
                  cursor: pointer;
                  position: absolute;
                  min-width: auto;
                  text-align: center;
                  background: var(--mac-bg-default);
                  color: var(--mac-text-color-month-link);
                  font-size: var(--text-xs);
                  letter-spacing: var(--mac-letter-spacing-wide);
                  text-transform: var(--mac-text-transform);
                  padding: var(--mac-padding-month-link);
                  box-shadow: none;
                  border-color: var(--mac-text-color-month-link);
                  transition: var(--transition);

                  @media (max-width: 768px) {
                    display: flex;
                    justify-content: center;
                    position: static;
                  }

                  &:hover {
                    background-color: var(--mac-month-link-bg-hover);
                    color: var(--mac-text-color-month-link-hover);
                    border-color: var(--mac-month-link-bg-hover);

                    a.mec-load-month-link {
                      color: var(--mac-text-color-month-link-hover);

                      i {
                        color: var(--mac-text-color-month-link-hover);
                      }
                    }
                  }

                  a.mec-load-month-link {
                    pointer-events: none;
                    line-height: var(--mac-line-height-tight);
                    color: var(--mac-text-color-month-link);
                    font-size: var(--text-xs);
                    font-weight: var(--mac-font-weight-bold);
                    letter-spacing: var(--mac-letter-spacing-wide);
                    text-transform: var(--mac-text-transform);
                    transition: var(--transition);

                    i {
                      color: var(--mac-text-color-month-link);
                      font-size: calc(var(--text-xs) * 0.75);
                      cursor: pointer;
                      font-weight: var(--mac-font-weight-bold);
                      vertical-align: middle;
                    }
                  }
                }

                .mec-previous-month {
                  left: 0;
                  top: 0;

                  @media (max-width: 768px) {
                    justify-content: start;
                    text-align: left;
                  }
                }

                .mec-next-month {
                  right: 0;
                  top: 0;

                  @media (max-width: 768px) {
                    justify-content: end;
                    text-align: right;
                  }
                }

                .mec-calendar-header {
                  margin-top: 0.25em;
                  margin-bottom: var(--content-gap);

                  @media (max-width: 768px) {
                    order: -1;
                    margin: 0;
                    width: 100%;
                    display: flex;
                    flex-direction: column;
                    gap: 0;
                    align-items: center;
                    justify-content: center;
                  }

                  h2 {
                    margin-bottom: 0;
                    font-size: var(--text-l);
                    color: var(--mac-text-color-heading);
                    line-height: var(--mac-line-height-tight);
                    font-weight: var(--heading-font-weight);
                    text-align: center;
                    text-transform: var(--mac-text-transform);
                    letter-spacing: 0.05em;
                  }
                }
              }
            }

            .mec-calendar-table {
              min-width: var(--content-width);
              min-height: unset;
              border-radius: var(--radius);
              border: var(--border);
              box-shadow: var(--box-shadow-m);

              .mec-month-container {
                dl {
                  display: table;
                  margin: 0;
                  border: none;
                  padding: 0;
                  table-layout: fixed;

                  dt {
                    display: table-cell;
                    transition: var(--transition);
                    background: transparent;
                    border-radius: 0;
                    font-size: var(--text-m);
                    width: auto;
                    height: auto;
                    line-height: var(--mac-line-height-tight);
                    text-align: left;
                    color: var(--mac-text-color-heading);
                    border: none;
                    border-left: var(--border);
                    padding: var(--mac-padding-cell);

                    &:first-child {
                      border-left: none;
                    }
                  }
                }

                .mec-calendar-table-head {
                  background: var(--mac-bg-header);
                  width: 100%;
                  border: var(--border);
                  border-color: var(--primary-dark);
                  border-radius: var(--radius) var(--radius) 0 0;

                  .mec-calendar-day-head {
                    background: transparent;
                    height: unset;
                    line-height: 2;
                    border: none;
                    color: var(--mac-text-color-heading);
                    border-left: var(--border);
                    border-color: var(--primary-dark);

                    &:first-child {
                      border-left: none;
                    }
                  }
                }

                .mec-calendar-row {
                  line-height: var(--mac-line-height-tight);
                  min-height: var(--mac-cell-min-height);
                  border: var(--border);
                  border-top: none;
                  background: transparent;

                  &:last-child {
                    border-radius: 0 0 var(--radius) var(--radius);

                    dt:first-child {
                      border-radius: 0 0 0 var(--radius);
                    }

                    dt:last-child {
                      border-radius: 0 0 var(--radius) 0;
                    }
                  }

                  &:nth-child(2) dt {
                    &:first-child,
                    &:last-child {
                      border-radius: 0;
                    }
                  }

                  .mec-table-nullday {
                    color: var(--mac-text-color-disabled);
                    font-weight: var(--mac-font-weight);
                    background: var(--mac-bg-disabled);
                  }

                  .mec-calendar-day {
                    font-size: var(--text-m);
                    color: var(--mac-text-color-heading);
                    font-weight: var(--mac-font-weight);
                    background: var(--mac-bg-default);

                    &.mec-selected-day {
                      color: var(--mac-text-color-accent);
                      font-weight: var(--mac-font-weight-bold);
                      border-bottom: var(--border);
                      border-bottom-width: var(--mac-today-cell-accent-width);
                      border-bottom-color: var(--mac-today-cell-accent-color);
                      background: var(--mac-bg-accent-light);
                    }

                    div:first-child {
                      font-weight: var(--mac-font-weight);
                      margin-bottom: var(--mac-cell-gap);

                      &.mec-color {
                        color: var(--mac-text-color-accent);
                      }
                    }

                    .ended-relative {
                      a.mec-monthly-tooltip {
                        border: none;
                        padding: 0;
                        display: flex;
                        text-decoration: none;
                        transition: var(--transition);

                        &:hover h4.mec-event-title {
                          text-decoration: none;
                          color: var(--mac-text-color-accent-hover);
                        }

                        h4.mec-event-title {
                          font-size: var(--text-s);
                          font-weight: var(--mac-font-weight-bold);
                          margin: 0;
                          color: var(--mac-text-color-dark);
                          line-height: var(--h4-line-height);
                          font-style: normal;
                          letter-spacing: normal;
                          clear: none;
                          transition: none;
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}

/// MEC - Speaker
// Speaker Modal
// div.lity.lity-opened.lity-inline {}
div.lity.lity-inline {
  --mac-modal-z-index: 999999;
  --mac-modal-bg-overlay: var(--neutral-trans-80);
  --mac-modal-max-width: var(--width-l);
  --mac-modal-padding: var(--fr-card-padding);

  --mac-font-weight: 400;
  --mac-font-weight-bold: 700;
  --mac-line-height-tight: 1;
  --mac-font-size-title: var(--h3);
  --mac-font-size-job: var(--text-m);
  --mac-font-size-icon: var(--text-xl);

  --mac-text-color-heading: var(--primary-ultra-dark);
  --mac-text-color-job: var(--primary-dark);
  --mac-text-color-accent: var(--primary);
  --mac-text-color-accent-hover: var(--primary-hover);
  --mac-text-color-modal-close: var(--white);
  --mac-bg-default: var(--white);

  --mac-radius: var(--radius);
  --mac-gap: var(--fr-card-gap);

  z-index: var(--mac-modal-z-index) !important;
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  white-space: nowrap;
  background: var(--mac-modal-bg-overlay);
  outline: none !important;
  transition: var(--transition);

  div.lity-wrap {
    z-index: 9990;
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    text-align: center;
    outline: none !important;

    &::before {
    }
    &::after {
    }

    div.lity-container {
      max-width: var(--mac-modal-max-width);
      width: 100%;
      z-index: 9992;
      position: relative;
      text-align: left;
      vertical-align: middle;
      display: inline-block;
      white-space: normal;
      max-height: 100%;
      outline: none !important;

      div.lity-content {
        z-index: 9993;
        width: 100%;
        transform: scale(1);
        transition: transform var(--transition);

        div.mec-hourly-schedule-speaker-info,
        div.mec-speaker-info {
          overflow: auto;
          max-height: 80vh !important;
          border-radius: var(--mac-radius);
          background: var(--mac-bg-default);
          padding: var(--mac-modal-padding);
          border: none;
          display: grid;
          grid-template-columns: var(--grid-1-2);
          gap: var(--mac-gap);
          align-items: stretch;

          @media (max-width: 992px) {
            grid-template-columns: var(--grid-2-3);
          }

          @media (max-width: 768px) {
            grid-template-columns: var(--grid-1);
          }

          div.mec-hourly-schedule-speaker-thumbnail {
            float: left;
            max-width: 100%;
            width: 100%;
            display: flex;
            border-radius: var(--mac-radius);

            img {
              width: 100%;
              height: auto;
              max-width: 100%;
              outline: none;
              vertical-align: middle;
              display: flex;
              border-radius: var(--mac-radius);
            }
          }

          div.mec-hourly-schedule-speaker-details {
            float: left;
            width: 100%;
            padding-left: 0;
            display: flex;
            flex-direction: column;
            gap: calc(var(--mac-gap) / 2);

            div.mec-hourly-schedule-speaker-name {
              font-family: var(--ff-body);
              font-weight: var(--heading-font-weight);
              font-size: var(--mac-font-size-title);
              line-height: var(--h3-line-height);
              color: var(--mac-text-color-heading);
              text-transform: capitalize;
              display: flex;
            }

            div.mec-hourly-schedule-speaker-job-title.mec-color {
              color: var(--mac-text-color-job);
              font-family: var(--ff-body);
              font-size: var(--mac-font-size-job);
              line-height: var(--text-line-height);
              margin-bottom: 0;
              margin: 0;
              font-weight: var(--mac-font-weight-bold);
            }

            div.mec-hourly-schedule-speaker-contact-information {
              display: flex;
              flex-direction: row;
              gap: var(--mac-gap);
              flex-wrap: wrap;

              a {
                display: flex;
                flex-direction: column;
                gap: 0;
                align-items: center;
                justify-content: center;
                width: auto;
                height: auto;
                background: transparent;
                padding: 0;
                transition: var(--transition);
                border-radius: var(--mac-radius);

                &:hover {
                  background: transparent;
                }

                i {
                  color: var(--mac-text-color-accent);
                  background: transparent;
                  line-height: 1;
                  margin: 0;
                  width: auto;
                  height: auto;
                  display: flex;
                  text-align: center;
                  transition: var(--transition);
                  font-size: var(--mac-font-size-icon);
                  cursor: pointer;
                  border-radius: var(--mac-radius);
                  background: transparent;
                  padding: 0;

                  &:hover {
                    color: var(--mac-text-color-accent-hover);
                    scale: 1.2;
                  }

                  &::before {
                  }
                }
              }
            }

            div.mec-hourly-schedule-speaker-description {
              font-family: var(--ff-body);
              font-size: var(--mac-font-size-job);
              font-weight: var(--mac-font-weight);
              color: var(--mac-text-color-job);
              line-height: var(--text-line-height);
              text-align: left;

              p {
              }
            }
          }
        }
      }

      button.lity-close {
        position: fixed;
        top: 2.4rem;
        right: 2.4rem;
        background: transparent;
        color: var(--mac-text-color-modal-close);
        font-size: 2em;
        border: var(--border);
        border-radius: 0;
        padding: 0;
        width: auto;
        height: auto;
        line-height: 1;
        z-index: 9999;
        cursor: pointer;
        text-decoration: none;
        text-align: center;
        font-style: normal;
        font-family: var(--ff-body);
        text-shadow: none;
        outline: none;
        box-shadow: none;

        @media (max-width: 992px) {
          top: 0.8em;
          right: 0.8rem;
        }
      }
    }
  }
}

/// MEC - Single
// (Style = Default)
body.single-mec-events {
  &.mec-events-template-default.mec-theme-bricks {
  }
  --border: var(--border-width) var(--border-style) var(--primary-ultra-dark-trans-20);

  /* Section */
  section#main-content.mec-container {
    margin: 0;
    padding: 0;
    width: 100%;
    max-width: 100%;
    padding-block: var(--section-padding-block);
    padding-top: var(--section-padding-block);
    padding-bottom: var(--section-padding-block);

    @media only screen and (min-width: 1200px) {
      padding-left: var(--gutter);
      padding-right: var(--gutter);
    }

    &::before {
    }
    &::after {
    }

    div.mec-wrap.colorskin-custom {
      margin: 0;
      padding: 0;
      margin-inline: auto;
      width: 100%;
      color: var(--neutral);
      font-weight: 400;

      article.mec-single-event {
        margin: 0;
        padding: 0;
        width: 100%;
        display: grid;
        grid-template-columns: var(--grid-2-1);
        align-items: stretch;
        gap: 0;

        @media only screen and (min-width: 1200px) {
          margin: 0;
        }

        @media (max-width: 992px), (max-width: 768px) {
          display: grid;
          grid-template-columns: var(--grid-1);
          gap: var(--grid-gap);
        }

        &::before,
        &::after {
          position: absolute;
          display: none;
        }

        /* Breadcrumbs */
        div.mec-breadcrumbs {
          margin: 0;
          padding: 0;
          margin-bottom: var(--fr-card-gap);
          grid-column: span 3;
          font-size: var(--text-xs);
          color: var(--primary-ultra-dark);
          letter-spacing: 0;
          text-transform: none;
          font-weight: 400;
          border: none;
          border-radius: var(--radius);

          display: none;

          @media (max-width: 992px), (max-width: 768px) {
            margin: 0;
            grid-column: span 1;
          }

          div.mec-address {
            display: flex;
            gap: 1ex;
            flex-wrap: wrap;

            @media (max-width: 992px) {
              gap: 0.5ex;
            }

            a,
            span.mec-current {
              text-decoration: none;
              font-weight: 400;
              background-color: transparent;
              border-radius: 0;
            }

            a {
              color: var(--primary-ultra-dark);
              transition: var(--transition);
              text-decoration: underline;
              text-decoration-color: transparent;
              text-underline-offset: 0.2em;
              text-decoration-thickness: 1.5px;

              &:hover {
                color: var(--primary-hover);
              }
            }

            span.mec-current {
              color: var(--primary);
              font-weight: 700;
            }

            i.mec-color.mec-sl-arrow-right {
              padding: 0;
              margin: 0 0.5ex;
              color: var(--primary-ultra-dark-trans-50);
              font-weight: 700;
              font-size: 0.5em;
              line-height: 1;
              height: 1em;
              vertical-align: baseline;
              translate: 0 calc(var(--text-m) * var(--text-line-height) * 0.25);

              &::before {
              }
            }
          }
        }

        /* Main */
        div.col-md-8 {
          margin: 0;
          padding: 0;
          width: 100%;
          display: flex;
          flex-direction: column;
          gap: var(--grid-gap);

          @media only screen and (min-width: 1200px) {
            padding-left: 0;
            padding-right: 0;
            min-height: unset;
          }

          @media (min-width: 961px) {
            float: unset;
          }

          /* Images */
          div.mec-events-event-image {
            margin: 0;
            padding: 0;
            border: unset;

            div.mec-event-gallery-wrapper {
              display: flex;
              flex-direction: column;
              gap: var(--fr-card-gap);
              align-items: start;

              div.mec-event-gallery-image-list-wrapper {
                margin: 0;
                padding: 0;
                min-width: unset;

                ul.mec-event-gallery-list {
                  margin: 0;
                  padding: 0;
                  display: grid;
                  grid-template-columns: var(--grid-8);
                  gap: calc(var(--fr-card-gap) / 2);
                  align-items: stretch;

                  @media (max-width: 992px), (max-width: 768px) {
                    grid-template-columns: var(--grid-4);
                  }

                  li {
                    margin: 0;
                    padding: 0;
                    display: flex;
                    width: 100%;
                    height: auto;
                    aspect-ratio: 4/3;
                    border-radius: var(--radius);
                    overflow: hidden;
                    border: var(--border);
                    box-shadow: none;
                    transition: var(--transition);

                    &:hover {
                      border-color: var(--primary-hover);
                      translate: 0 -0.5ex;
                    }

                    img {
                      width: 100%;
                      height: 100%;
                      object-fit: cover;
                      display: flex;
                      flex-grow: 1;
                      border-radius: unset;
                    }
                  }
                }
              }

              div.mec-event-gallery-image {
                border-radius: var(--radius);
                overflow: hidden;
                order: -1;
                border: var(--border);

                img {
                  width: 100%;
                  height: auto;
                }
              }
            }
          }

          /* Content */
          div.mec-event-content {
            margin: 0;
            padding: 0;
            display: flex;
            flex-direction: column;
            gap: var(--content-gap);

            h1.mec-single-title {
              margin: 0;
              padding: 0;
              font-size: var(--h1);
              color: var(--h1-color);
              line-height: var(--h1-line-height);
              font-weight: var(--heading-font-weight);
              text-wrap: pretty;
            }

            div.mec-single-event-description.mec-events-content {
              margin: 0;
              padding: 0;
              display: flex;
              flex-direction: column;
              gap: var(--fr-card-gap);

              p {
                margin: 0;
                padding: 0;
                font-size: var(--text-m);
                color: var(--text-color);
                line-height: var(--text-line-height);
                text-wrap: pretty;
                font-weight: 400;
              }

              figure {
                margin: 0;
                padding: 0;
              }

              h1,
              h2,
              h3,
              h4,
              h5,
              h6 {
                margin: 0;
                padding: 0;
                font-weight: var(--heading-font-weight);
                font-style: normal;
                letter-spacing: normal;
                clear: none;
              }

              h1 {
                font-size: var(--h1);
                color: var(--h1-color);
                line-height: var(--h1-line-height);
                font-weight: var(--heading-font-weight);
              }

              h2 {
                font-size: var(--h2);
                color: var(--h2-color);
                line-height: var(--h2-line-height);
                font-weight: var(--heading-font-weight);
              }

              h3 {
                font-size: var(--h3);
                color: var(--h3-color);
                line-height: var(--h3-line-height);
                font-weight: var(--heading-font-weight);
              }

              h4 {
                font-size: var(--h4);
                color: var(--h4-color);
                line-height: var(--h4-line-height);
                font-weight: var(--heading-font-weight);
              }

              h5 {
                font-size: var(--h5);
                color: var(--h5-color);
                line-height: var(--h5-line-height);
                font-weight: var(--heading-font-weight);
              }

              h6 {
                font-size: var(--h6);
                color: var(--h6-color);
                line-height: var(--h6-line-height);
                font-weight: var(--heading-font-weight);
              }

              ul {
              }

              ol {
              }

              li {
                font-size: var(--text-m);
                color: var(--text-color);
                line-height: var(--text-line-height);
                font-weight: 400;

                &::marker {
                  color: var(--primary);
                }
              }
            }
          }

          /* Mobile Info */
          div.mec-event-info-mobile {
            padding-left: 0;
          }

          /* Add to Calendar */
          div.mec-event-export-module.mec-frontbox {
            margin: 0;
            padding: var(--fr-card-padding);
            display: flex;
            overflow: hidden;
