            @include mac-mec-btn-style;
            --btn-background: transparent;
            --btn-text-color: var(--base);
            --btn-font-size: var(--text-s);
            --btn-border-color: var(--base);
            --btn-background-hover: var(--base-hover);
            --btn-text-color-hover: var(--base-ultra-light);
            --btn-border-color-hover: var(--base-hover);

            i {
              padding-right: 0;
              padding-left: 0;
              speak: none;
              font-family: FontAwesome;
              font-style: normal;
              line-height: 1;
              font-weight: normal;
              text-transform: none;
              font-variant: normal;
              -webkit-font-smoothing: antialiased;
              -moz-osx-font-smoothing: grayscale;

              &::before {
              }
            }
          }
        }

        li.mec-previous-events {
          a.mec-color.mec-bg-color-hover.mec-border-color {
            i.mec-fa-long-arrow-left {
              &::before {
              }
            }
          }
        }
        li.mec-next-event {
          @media (max-width: 768px) {
            --btn-background: var(--base);
            --btn-text-color: var(--base-ultra-light);
            --btn-border-color: var(--base);
          }

          a.mec-color.mec-bg-color-hover.mec-border-color {
            i.mec-fa-long-arrow-right {
              &::before {
              }
            }
          }
        }
      }
    }
  }
}

/// MEC - Fixes

// (Single Template Style = Default)

// fix speaker without avatar
.mec-speaker-info.mec-hourly-schedule-speaker-info {
  &:not(:has(.mec-hourly-schedule-speaker-thumbnail + .mec-hourly-schedule-speaker-details)) {
    .mec-hourly-schedule-speaker-details {
      grid-column: span 2;
    }
  }
}

// (Calendar Shortcode Skin = Calendar/Monthly View, Style = Simple)

// fix calendar cell
dt.mec-calendar-day {
  div.simple-skin-ended {
    margin-block-start: 1ex;
  }
}
h1.mec-single-title {
  text-wrap: pretty !important;
  font-size: calc(var(--h2) * 1.2) !important;
}

// hide empty calendar row
.mec-calendar-row:empty {
  display: none !important;
}





